INSERT INTO `categories` (`id`, `name_category`, `description`, `slug`, `active`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Hỗ trợ vùng cao, vùng khó khăn', 'Là những chương trình Dân Vận đến các vùng sâu vùng xa để giúp đỡ những bà con có thể cải thiện đời sống', 'ho-tro-vung-cao-vung-kho-khan', 1, '2023-04-27 14:14:17', '2023-05-18 19:53:35', NULL),
(2, 'Hoạt động thiện nguyện', '<p>Chuỗi hoạt động hỗ trợ c&aacute;c trung t&acirc;m trại trễ mồ c&ocirc;i, viện dưỡng l&atilde;o, trung t&acirc;m hướng nghiệp người khuyết tật,...</p>', 'hoat-dong-thien-nguyen', 1, '2023-05-11 18:32:58', '2023-05-11 18:32:58', NULL),
(3, 'Suất ăn miễn phí', 'Những chương trình phát xuất ăn miễn phí cho những cô chú lao công, vô gia cư, hoàn cảnh khó khăn trên địa bàn thành phố Đà Nẵng', 'suat-an-mien-phi', 1, '2023-04-27 14:14:17', '2023-04-27 14:14:17', NULL),
(4, 'Hoạt động tình nguyện', 'Đây là danh mục tập hợp những hoạt động tình nguyện mà Hand In Hand đã tổ chức và tham gia trên địa bàn thành phố Đà Nẵng.', 'hoat-dong-tinh-nguyen', 1, '2023-04-28 08:47:26', '2023-04-28 09:02:34', NULL),
(7, 'Hoạt động kỹ năng', '<p>Đ&acirc;y l&agrave; chuỗi hoạt động nhằm để trau dồi kỹ năng cho th&agrave;nh vi&ecirc;n Nh&oacute;m Từ Thiện 3T, n&acirc;ng cao t&igrave;nh th&acirc;n đo&agrave;n kết, đồng đội, c&ugrave;ng nhau giải quyết vấn đề, vượt qua kh&oacute; khăn.</p>', 'hoat-dong-ky-nang', 1, '2023-05-11 18:28:31', '2023-06-09 04:34:47', '2023-06-09 04:34:47');

