--
-- Cấu trúc bảng cho bảng `projects`
--

CREATE TABLE `projects` (
  `id` int(11) NOT NULL,
  `id_category` int(11) NOT NULL,
  `id_user_create` int(11) NOT NULL,
  `name_project` varchar(255) NOT NULL,
  `slug` varchar(255) NOT NULL,
  `thumbnail` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `short_desc` text DEFAULT NULL,
  `target` double DEFAULT NULL,
  `donation` double DEFAULT NULL,
  `donate_for` varchar(255) DEFAULT NULL,
  `phone` varchar(11) DEFAULT NULL,
  `link_fb` varchar(255) DEFAULT NULL,
  `date_action` int(11) NOT NULL,
  `fund` double DEFAULT NULL,
  `status` int(1) NOT NULL,
  `active` int(1) NOT NULL,
  `finished_at` timestamp NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `projects`
--

INSERT INTO `projects` (`id`, `id_category`, `id_user_create`, `name_project`, `slug`, `thumbnail`, `description`, `short_desc`, `target`, `donation`, `donate_for`, `phone`, `link_fb`, `date_action`, `fund`, `status`, `active`, `finished_at`, `created_at`, `updated_at`, `deleted_at`) VALUES
(2, 3, 2, '123', '123', '123-3640.jpg', '<p>s</p>', '', 0, 0, NULL, NULL, NULL, 1708534800, 0, 1, 0, NULL, '2023-05-19 01:11:14', '2023-05-21 18:12:40', '2023-05-21 18:12:40'),
(3, 1, 2, 'Chương trình nấu ăn cho em', 'chuong-trinh-nau-an-cho-em', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686290678/project/chuong-trinh-nau-an-cho-em-2241.jpg', '<p>Nh&oacute;m cũng tổ chức những buổi học kỹ năng cho c&aacute;c em tại đ&acirc;y như: c&aacute;ch n&oacute;i lời cảm ơn v&agrave; xin lỗi, ph&acirc;n loại r&aacute;c bảo vệ m&ocirc;i trường, kỹ năng đ&aacute;nh răng đ&uacute;ng c&aacute;ch v&agrave; đặc biệt sau buổi học c&aacute;c em đ&atilde; c&oacute; thể tự m&igrave;nh ph&acirc;n loại r&aacute;c v&agrave; được tặng b&oacute;t đ&aacute;nh răng để thực h&agrave;nh tại chỗ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2022/09/3T-12-1024x683.jpg\" style=\"height:680px; width:1020px\" /></p>\r\n\r\n<p>Hướng dẫn c&aacute;c em nhỏ kỹ năng đ&aacute;nh răng</p>\r\n\r\n<p>Ngo&agrave;i ra, Nh&oacute;m Hand In Hand Việt &ndash; H&agrave;n c&ograve;n tổ chức nhiều hoạt động sinh hoạt chung với đồng b&agrave;o tại đ&acirc;y như: chiếu phim, nấu hơn 100 suất ăn miễn ph&iacute; cho tất cả trẻ em tại n&oacute;c, cắt t&oacute;c miễn ph&iacute;, tặng giống vật nu&ocirc;i cho 5 hộ gia đ&igrave;nh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn, 54 phần qu&agrave; cho 54 hộ gia đ&igrave;nh bao gồm quần &aacute;o cũ, nhu yếu phẩm, mền,&hellip; 100 phần qu&agrave; cho 100 em nhỏ bao gồm (đồng phục, cặp, &aacute;o ấm, ủng đi mưa,&hellip;. Nhằm g&oacute;p phần giữ g&igrave;n sự sạch đẹp cho cuộc sống của b&agrave; con, Nh&oacute;m từ thiện đ&atilde; tặng v&agrave; đặt 3 th&ugrave;ng r&aacute;c d&acirc;n sinh cho b&agrave; con tại n&oacute;c Tu Nương, x&atilde; Tr&agrave; Tập.</p>\r\n\r\n<p>V&agrave; đặc biệt, Nh&oacute;m đ&atilde; ho&agrave;n th&agrave;nh c&acirc;y cầu treo d&agrave;i 15m bằng sắt kết hợp d&acirc;y văng trị gi&aacute; hơn 50 triệu đồng bắt qua suối Tu Nương để b&agrave; con kh&ocirc;ng c&ograve;n lo sợ mỗi khi m&ugrave;a mưa b&atilde;o về v&agrave; thay lại to&agrave;n bộ m&aacute;i nh&agrave; cho hộ gia đ&igrave;nh anh Trần Văn Vương &ndash; hộ gia đ&igrave;nh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn tại đ&acirc;y.</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2022/09/3T-14-1024x683.jpg\" style=\"height:680px; width:1020px\" /></p>\r\n\r\n<p>Hơn 100 suất ăn M&igrave; quảng g&agrave; được nấu cho c&aacute;c em nhỏ</p>', 'Nhóm cũng tổ chức những buổi học kỹ năng cho các em tại đây như: cách nói lời cảm ơn và xin lỗi, phân loại rác bảo vệ môi trường, kỹ năng đánh răng đúng cách và đặc biệt sau buổi học các em đã có thể tự mình phân loại rác và được tặng bót đánh răng để thực hành tại chỗ.', 50000000, 2400000, 'Nhóm từ thiện Hand In Hand Việt - Hàn', '0343428511', 'https://www.facebook.com/Tuthien.HandInHandVietHan', 1687712400, 20000000, 0, 1, NULL, '2023-05-19 01:46:26', '2023-06-13 17:43:12', NULL),
(4, 3, 2, 'Quán Cơm Xã Hội Nụ Cười', 'quan-com-xa-hoi-nu-cuoi', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1684948771/project/quan-com-xa-hoi-nu-cuoi-3330.jpg', '<p>Qu&aacute;n cơm x&atilde; hội Nụ Cười 6 l&agrave; qu&aacute;n cơm 2.000đ tiếp theo nằm trong dự &aacute;n trợ gi&uacute;p suất ăn gi&aacute; rẻ của Quỹ từ thiện B&ocirc;ng Sen. Qu&aacute;n khai trương ng&agrave;y 19/10/2013. Qu&aacute;n phục vụ c&aacute;c suất cơm trưa 2.000đ với đầy đủ c&aacute;c m&oacute;n mặn, canh, x&agrave;o, tr&aacute;ng miệng, cơm thoải m&aacute;i v&agrave; tr&agrave; đ&aacute; miễn ph&iacute;. Đối tượng phục vụ của qu&aacute;n l&agrave; người mưu sinh thu nhập thấp, người gi&agrave; neo đơn, người khuyết tật v&agrave; học sinh sinh vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn.</p>\r\n\r\n<hr />\r\n<p><strong>Chủ nhiệm qu&aacute;n:</strong>&nbsp;Mr. Nam Đồng</p>\r\n\r\n<p><strong>Địa chỉ:</strong>&nbsp;Số 11 Nguyễn Huy Lượng, P.14, quận B&igrave;nh Thạnh, TPHCM</p>\r\n\r\n<p><strong>Li&ecirc;n hệ</strong>: 0906.805.838 (Ms. Ch&acirc;u) hoặc 093.889.7154 (Ms. Huệ)</p>\r\n\r\n<p><strong>Email</strong>: quancomnucuoi6@gmail.com</p>\r\n\r\n<p>TK: Quỹ Từ thiện B&ocirc;ng Sen.STK: 508388 Ng&acirc;n h&agrave;ng thương mại cổ phần &Aacute; Ch&acirc;u (ACB) &ndash; Chi nh&aacute;nh S&agrave;i G&ograve;n</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<p><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2017/12/nc-65-1.jpg\" style=\"height:724px; width:1287px\" /></p>', 'Quán cơm xã hội Nụ Cười 6 là quán cơm 2.000đ tiếp theo nằm trong dự án trợ giúp suất ăn giá rẻ của Quỹ từ thiện Bông Sen. Quán khai trương ngày 19/10/2013', 3500000, 0, 'Quán Cơm Xã Hội Nụ Cười', NULL, NULL, 1687885200, 3500000, 1, 1, NULL, '2023-05-24 17:19:32', '2023-06-09 17:28:26', NULL),
(5, 4, 2, 'Giảng dạy tình nguyện tại Hope School', 'giang-day-tinh-nguyen-tai-hope-school', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686311191/project/giang-day-tinh-nguyen-tai-hope-school-5946.jpg', '<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">🥰TỔNG KẾT CHƯƠNG TR&Igrave;NH GIẢNG DẠY T&Igrave;NH NGUYỆN ĐỢT 02 TẠI HOPE SCHOOL🤗</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">👩&zwj;🏫Sau hơn 6 th&aacute;ng giảng dạy tại HOPE SCHOOL - Trường Hy Vọng (Địa chỉ: 01 Trần Quốc Vượng), c&aacute;c bạn gi&aacute;o vi&ecirc;n t&igrave;nh nguyện đ&atilde; ho&agrave;n th&agrave;nh tốt việc giảng dạy, hướng dẫn học tập cho c&aacute;c bạn nhỏ tại trường. Nh&oacute;m từ thiện Hand In Hand Việt - H&agrave;n rất vinh dự khi l&agrave; một đơn vị tham gia với kh&ocirc;ng chỉ l&agrave; c&aacute;c bạn th&agrave;nh vi&ecirc;n 3T m&agrave; c&ograve;n l&agrave; c&aacute;c bạn sinh vi&ecirc;n VKU.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">🥰Ng&agrave;y 02 th&aacute;ng 06, chương tr&igrave;nh tổng kết năm học tại Hope School đ&atilde; diễn ra, đồng thời đ&aacute;nh dấu hơn 1 học kỳ gắn b&oacute; giữa c&aacute;c gi&aacute;o vi&ecirc;n t&igrave;nh nguyện v&agrave; c&aacute;c bạn nhỏ tại Hope. Một quảng thời gian kh&ocirc;ng qu&aacute; d&agrave;i nhưng đủ cho thấy sự nhiệt huyết, kh&ocirc;ng ngại mưa gi&oacute; của c&aacute;c bạn tham gia.&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">Ban Điều H&agrave;nh Nh&oacute;m xin ch&acirc;n th&agrave;nh cảm ơn đến Hope School đ&atilde; tạo điều kiện, cũng như hỗ trợ đội ngũ gi&aacute;o vi&ecirc;n t&igrave;nh nguyện hết m&igrave;nh trong thời gian vừa qua v&agrave; gửi lời biết ơn s&acirc;u sắc đến đội ngũ gi&aacute;o vi&ecirc;n t&igrave;nh nguyện đ&atilde; tiếp lửa cho c&aacute;c em trong năm học 2022-2023.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">💙Trong những năm học tiếp theo, Nh&oacute;m từ thiện Hand In Hand Việt - H&agrave;n rất mong vẫn c&oacute; thể tiếp tục tham gia giảng dạy v&agrave; hỗ trợ học tập cho c&aacute;c bạn kh&ocirc;ng chỉ tại Hope School m&agrave; c&ograve;n tại những trung t&acirc;m, trường học t&igrave;nh nguyện, từ thiện kh&aacute;c trong địa b&agrave;n th&agrave;nh phố Đ&agrave; Nẵng.&nbsp; Cuối c&ugrave;ng, sự tham gia của c&aacute;c t&igrave;nh nguyện vi&ecirc;n l&agrave; nguồn động lực to lớn của Hand In Hand Việt - H&agrave;n.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">---------------------------------------------</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">► Follow Hand In Hand Việt H&agrave;n</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">Hotline: 0799 43 8511</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">Radio: </span></span></span><a href=\"https://bit.ly/Radio3T\" style=\"text-decoration:none\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1155cc\"><u>https://bit.ly/Radio3T</u></span></span></span></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">Youtube: </span></span></span><a href=\"https://bit.ly/YoutubeHIH3T\" style=\"text-decoration:none\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1155cc\"><u>https://bit.ly/YoutubeHIH3T</u></span></span></span></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">Instagram: </span></span></span><a href=\"https://bit.ly/instagramHIH3T\" style=\"text-decoration:none\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1155cc\"><u>https://bit.ly/instagramHIH3T</u></span></span></span></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">Tiktok: </span></span></span><a href=\"https://www.tiktok.com/@tuthien_hih3t\" style=\"text-decoration:none\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1155cc\"><u>https://www.tiktok.com/@tuthien_hih3t</u></span></span></span></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">Truy cập </span></span></span><a href=\"http://handinhandviethan.com\" style=\"text-decoration:none\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1155cc\"><u>http://handinhandviethan.com</u></span></span></span></a></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:10.5pt\"><span style=\"font-family:Roboto,sans-serif\"><span style=\"color:#1c2b33\">để cập nhật tin tức từ thiện mới nhất của nh&oacute;m</span></span></span></p>', 'Tại tuyến kênh Phèn, xã Vĩnh Viễn, Long Mỹ, Hậu Giang có 1 đoạn đường chỉ dài 1km đi vào nơi sinh sống của khoảng 30 hộ dân nhưng mấy chục năm lúc nào cũng trong tình trạng lầy lội khiến việc đi lại của người dân vô cùng vất vả', 0, 0, 'Nhóm từ thiện Hand In Hand Việt - Hàn', NULL, NULL, 1702573200, 0, 1, 1, NULL, '2023-06-09 11:46:33', '2023-06-09 05:26:26', NULL),
(6, 3, 2, 'Suất Ăn Nghĩa Tình', 'suat-an-nghia-tinh', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686311551/project/quan-yen-vui-tan-trieu-%E2%80%93-ha-noi-6821.jpg', '<p>Qu&aacute;n cơm 2000 đồng thứ 15 ra đời trong những ng&agrave;y th&aacute;ng kh&oacute; khăn nhất của đất nước, khắp nơi dịch bệnh, th&agrave;nh phố H&agrave; nội tạm ngưng dịch vụ thiết yếu. Qu&aacute;n nằm ngay cổng sau của Bệnh viện K T&acirc;n Triều, nơi c&oacute; rất nhiều bệnh nh&acirc;n v&agrave; người nu&ocirc;i bệnh cả nước tụ hội về, nhu cầu suất ăn dinh dưỡng m&agrave; gi&aacute; rẻ rất lớn.</p>\r\n\r\n<p dir=\"auto\">Lặng lẽ T&acirc;n Triều</p>\r\n\r\n<p>Qu&aacute;n Y&ecirc;n Vui T&acirc;n Triều &ndash; H&agrave; nội lặng lẽ b&aacute;n thử buổi đầu ti&ecirc;n. N&oacute;i theo ng&ocirc;n ngữ thời thượng th&igrave; việc n&agrave;y kh&ocirc;ng theo &ldquo;trend&rdquo;</p>\r\n\r\n<p>Quỹ Từ thiện B&ocirc;ng Sen đang dồn hết sức lực cho việc ph&aacute;t qu&agrave;, nấu cơm cho đồng b&agrave;o v&ugrave;ng dịch Saigon. Cả nước hướng về Saigon th&igrave; ở H&agrave; nội, qu&aacute;n cơm 2.000đ thứ 15 của Quỹ Từ thiện B&ocirc;ng Sen lặng lẽ nh&oacute;m bếp, nổi lửa nấu những bữa cơm ấm n&oacute;ng cho những người cần thiết v&agrave;o h&ocirc;m qua 20/7/2021.</p>\r\n\r\n<p>Qu&aacute;n ra đời trong những ng&agrave;y th&aacute;ng kh&oacute; khăn nhất của đất nước, khắp nơi dịch bệnh, th&agrave;nh phố H&agrave; nội tạm ngưng dịch vụ thiết yếu.</p>\r\n\r\n<p>Qu&aacute;n nằm ngay cổng sau của Bệnh viện K T&acirc;n Triều, nơi c&oacute; rất nhiều bệnh nh&acirc;n v&agrave; người nu&ocirc;i bệnh cả nước tụ hội về, nhu cầu suất ăn dinh dưỡng m&agrave; gi&aacute; rẻ rất lớn. Vị tr&iacute; của qu&aacute;n thật qu&aacute; đ&uacute;ng chỗ, qu&aacute; đ&uacute;ng đối tượng.</p>\r\n\r\n<p>Nhưng ẩn chứa đằng sau những suất cơm ngon l&agrave;nh, hơn bao giờ hết, Quỹ B&ocirc;ng Sen v&agrave; ban Quản l&yacute; qu&aacute;n kỳ vọng rất nhiều v&agrave;o QYV T&acirc;n Triều, nơi sẽ b&aacute;n th&ecirc;m những chất liệu tạo dựng b&igrave;nh an v&agrave; hạnh ph&uacute;c. Chất liệu đ&oacute; l&agrave; g&igrave;? Đ&oacute; l&agrave; những kỹ năng thực tập để ch&uacute;ng ta tiếp cận với Y&ecirc;n v&agrave; Vui, (từ H&aacute;n Việt l&agrave; An v&agrave; Lạc), đ&oacute; l&agrave; khả năng điều h&ograve;a hơi thở để trở về với hiện tại, khả năng chế ngự bản th&acirc;n, kiềm chế cơn giận v.v&hellip;</p>\r\n\r\n<p>V&agrave;o c&aacute;c buổi chiều, qu&aacute;n sẽ mời c&aacute;c huấn luyện vi&ecirc;n đến hướng dẫn bệnh nh&acirc;n v&agrave; người nh&agrave; bệnh nh&acirc;n kỹ năng mềm rất cần thiết để hỗ trợ điều trị. Những buổi huấn luyện hiệu quả đ&oacute; c&oacute; thể miễn ph&iacute; hoặc thu ph&iacute; tượng trưng 2.000đ.</p>\r\n\r\n<p dir=\"auto\">Tiếng Việt hay c&oacute; từ gh&eacute;p: &ldquo;đau khổ&rdquo;, &ldquo;bệnh hoạn&rdquo;. &ldquo;Đau&rdquo; th&igrave; y học chữa trị, nhưng &ldquo;khổ&rdquo; th&igrave; c&oacute; rất nhiều con đường để bớt khổ. &ldquo;Bệnh&rdquo; th&igrave; thầy thuốc giải quyết nhưng &ldquo;hoạn nạn&rdquo; th&igrave; cần những b&agrave;n tay x&atilde; hội ch&igrave;a ra để gi&uacute;p đỡ. Qu&aacute;n Y&ecirc;n Vui hi vọng sẽ b&aacute;n được sự y&ecirc;n vui cho tất cả mọi người&hellip;</p>\r\n\r\n<p dir=\"auto\">Qu&aacute;n Y&ecirc;n Vui T&acirc;n Triều &ndash; H&agrave; Nội</p>\r\n\r\n<p dir=\"auto\">Địa chỉ: Số 284 Tập thể trạm bơm th&ocirc;n Y&ecirc;n X&aacute;, x&atilde; T&acirc;n Triều, huyện Thanh Tr&igrave;, H&agrave; Nội<br />\r\nLi&ecirc;n hệ: 097 207 1234 (Mr.Sơn)<br />\r\nNg&agrave;y mở b&aacute;n thử 20/7/2021</p>\r\n\r\n<p dir=\"auto\">Ng&agrave;y hoạt động: Thứ 3 -Thứ 5 &ndash; Thứ 7</p>\r\n\r\n<p><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2021/07/223035094_4277418335658089_5875316782322677025_n.jpg\" style=\"height:1440px; width:1920px\" /></p>', 'Quán cơm 2000 đồng thứ 15 ra đời trong những ngày tháng khó khăn nhất của đất nước, khắp nơi dịch bệnh, thành phố Hà nội tạm ngưng dịch vụ thiết yếu.', 10000000, 0, 'Quán Yên Vui Tân Triều', '0972071234', NULL, 1690304400, 0, 0, 1, NULL, '2023-06-09 11:52:33', '2023-06-11 16:39:13', NULL),
(7, 1, 2, 'Trà Tập Nhịp Đập Yêu Thương', 'tra-tap-nhip-dap-yeu-thuong', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686313905/project/tra-tap-nhip-dap-yeu-thuong-9435.jpg', '<p>Qua ph&oacute;ng sự do nh&oacute;m Từ thiện Hand In Hand Việt H&agrave;n thực hiện v&agrave;o ng&agrave;y 09/07/2022 &ndash; 10/07/2022, ta c&oacute; thể thấy: c&aacute;i gọi l&agrave; nh&agrave; để ở chỉ được gh&eacute;p bằng những miếng gỗ kh&ocirc;ng chắc chắn, thậm ch&iacute; kh&ocirc;ng đủ che mưa che nắng v&agrave; dễ d&agrave;ng bị cuốn tr&ocirc;i khi c&oacute; trận b&atilde;o lớn k&eacute;o về.&nbsp;Những h&ocirc;m trời mưa to, căn nh&agrave; dột n&aacute;t tới mức cả nh&agrave; 6 người chỉ c&oacute; thể cố n&eacute;p v&agrave;o nhau để chợp mắt qua đ&ecirc;m.&nbsp;Khoảng c&aacute;ch từ nh&agrave; đến trường học của c&aacute;c b&eacute; ở đ&acirc;y l&agrave; 1 giờ 30 ph&uacute;t đến 2 giờ đi bộ nhưng ch&uacute;ng chẳng thể ngăn nổi sự kh&aacute;t khao &ldquo;đi t&igrave;m c&aacute;i chữ&rdquo; của c&aacute;c em. Tuy nhi&ecirc;n, đường đi chưa phải l&agrave; kh&oacute; khăn duy nhất mỗi m&ugrave;a mưa lũ về con suối y&ecirc;n ả hiền h&ograve;a dưới ch&acirc;n đồi lại l&agrave; thứ khiến bước ch&acirc;n c&aacute;c em ch&ugrave;n lại. Lũ về khiến nước d&acirc;ng đột ngột, chảy xiết k&eacute;o theo mọi thứ tr&ecirc;n đường n&oacute; đi qua khiến cho con s&ocirc;ng trở n&ecirc;n v&ocirc; c&ugrave;ng nguy hiểm. Mỗi l&uacute;c như vậy c&aacute;c b&eacute; sẽ phải nghỉ học v&agrave;i ng&agrave;y, đ&ocirc;i khi k&eacute;o d&agrave;i đến gần nửa th&aacute;ng. Thậm ch&iacute; đến cả người lớn cũng kh&ocirc;ng d&aacute;m mạo hiểm băng qua để l&ecirc;n nương hay ra huyện để mua lương thực, họ chỉ đ&agrave;nh chấp nhận ăn rau dại, t&igrave;m c&aacute;, t&igrave;m những thứ c&oacute; thể ăn tại xung quanh nơi sống để chống chọi qua m&ugrave;a b&atilde;o lũ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2022/08/2.00_03_29_13.Still014-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p>N&oacute;c Tu Nương &ndash; Tr&agrave; Tập &ndash; Nam Tr&agrave; My</p>\r\n\r\n<p>C&oacute; được cơ hội trải nghiệm sinh sống một khoảng thời gian với người d&acirc;n nơi đ&acirc;y, ch&uacute;ng t&ocirc;i mới c&oacute; thể chứng kiến v&agrave; thấu hiểu những th&ecirc; lương, những thiệt th&ograve;i m&agrave; b&agrave; con phải đối mặt hằng ng&agrave;y. Điện, nước, đường, trường học, trạm y tế tất cả đều v&ocirc; c&ugrave;ng kh&oacute; khăn v&agrave; thiếu thốn: Mạng lưới điện của nh&agrave; nước chưa tiếp cận được nơi v&ugrave;ng cao c&ugrave;ng xa n&agrave;y. Thế n&ecirc;n, muốn c&oacute; nguồn s&aacute;ng cho việc sinh hoạt người d&acirc;n phải sử dụng nguồn nước suối để tự sản xuất ra điện, đường d&acirc;y điện vắt ngang, trải d&agrave;i khắp con đường rừng, với những chỗ hở điện dễ g&acirc;y nguy hiểm đến t&iacute;nh mạng. Đ&aacute;nh đổi như vậy, nhưng lượng điện sản sinh ra vẫn kh&ocirc;ng đủ d&ugrave;ng.</p>\r\n\r\n<p>Đường ống dẫn nước sạch bị hở do thời gian, giờ đ&acirc;y nước sinh hoạt của người d&acirc;n đều bắt từ nguồn suối &ndash; nước từ suối kh&ocirc;ng hề đảm bảo vệ sinh lại c&ograve;n kh&ocirc;ng đủ nước cho sinh hoạt,&hellip;. Tại nơi đ&acirc;y kh&oacute; khăn chồng chất kh&oacute; khăn, thiếu thốn chồng chất thiếu thốn khi Trạm Y tế cũng kh&ocirc;ng c&oacute;, mỗi lần người d&acirc;n bị bệnh đều phải băng qua con đường rừng độc đạo để đến được trạm Y tế gần nhất tại trung t&acirc;m x&atilde;.</p>\r\n\r\n<p>Mong ước nhỏ nhoi về chiếc cặp mới, một quyển vở, những c&acirc;y b&uacute;t m&agrave;u tưởng chừng qu&aacute; đỗi b&igrave;nh thường nhưng n&oacute; c&oacute; lẽ lại l&agrave; điều g&igrave; đ&oacute; thật xa vời đối với c&aacute;c em, khi nơi đ&acirc;y c&ograve;n bao điều kh&aacute;c cần lắng lo hơn. C&aacute;i ngh&egrave;o cứ quẩn quanh bản l&agrave;ng ấy từ năm n&agrave;y sang năm kh&aacute;c. &ldquo;Trẻ em l&agrave; mầm non tương lai của đất nước&rdquo;, n&ecirc;n cả x&atilde; hội cần chung tay để cải thiện chất lượng cuộc sống cho c&aacute;c em nhỏ v&ugrave;ng cao. Ch&iacute;nh v&igrave; thế, nh&oacute;m mạnh dạn đề xuất những c&ocirc;ng t&aacute;c thực hiện tr&ecirc;n Tr&agrave; Tập nhằm n&acirc;ng cao đời sống người d&acirc;n, tạo cơ hội để c&aacute;c em nhỏ c&oacute; tương lai tươi s&aacute;ng hơn như sau:</p>\r\n\r\n<h3>&ndash; X&acirc;y dựng một c&acirc;y cầu d&agrave;i 15m với dự tr&ugrave; kinh ph&iacute; ban đầu l&agrave; 54.889.000 đ.</h3>\r\n\r\n<h3>&ndash; Tặng 05 suất học bổng khuyến học cho c&aacute;c em học sinh ngh&egrave;o vượt kh&oacute;: 200.000 đồng/ suất.</h3>\r\n\r\n<h3>&ndash; Tặng 54 phần qu&agrave; thiết thực cho c&aacute;c hộ d&acirc;n tại n&oacute;c : 150.000 đồng/ suất.</h3>\r\n\r\n<h3>&ndash; Tặng 100 phần qu&agrave; cho c&aacute;c b&eacute; tại n&oacute;c : 150.000 đồng/ suất&nbsp; (bao gồm &aacute;o ấm v&agrave; dụng cụ học tập)</h3>\r\n\r\n<h3>&ndash; Sửa chữa v&agrave; tặng 2 lưới b&oacute;ng đ&aacute; + 1 quả b&oacute;ng cho c&aacute;c b&eacute;.</h3>\r\n\r\n<h3>&ndash; Tổ chức chiếu phim v&agrave; nấu 100 suất ăn miễn ph&iacute; cho c&aacute;c b&eacute;.</h3>\r\n\r\n<h3>&ndash; L&agrave;m tủ s&aacute;ch, truyện cho c&aacute;c em nhỏ với mục ti&ecirc;u 50 cuốn truyện.</h3>\r\n\r\n<h3>&ndash; Lợp lại m&aacute;i nh&agrave; cho gia đ&igrave;nh anh Hồ Văn Vương (gia đ&igrave;nh c&oacute; ho&agrave;n cảnh đặc biệt kh&oacute; khăn): 3.000.000 đồng</h3>\r\n\r\n<h3>&ndash; Cắt t&oacute;c cho đồng b&agrave;o v&agrave; học sinh Cadong.</h3>\r\n\r\n<h3>&ndash; Trồng hoa v&agrave; tu sửa lớp học.</h3>\r\n\r\n<h3>&ndash; Trồng lại cột cờ cho trường.</h3>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2022/08/2.00_07_59_11.Still011-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p>Trẻ em phải băng qua con suối nguy hiểm để đi học</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2022/08/2.00_08_02_18.Still012-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p>Niềm vui nhỏ của trẻ em nơi đ&acirc;y</p>\r\n\r\n<p>Để thực hiện h&oacute;a được đ&uacute;ng tiến độ v&agrave; mục ti&ecirc;u đề ra cho chương tr&igrave;nh Tr&agrave; Tập &ndash; Nhịp đập y&ecirc;u thương, Nh&oacute;m từ thiện Hand In Hand Việt H&agrave;n sẽ cần một số lượng kinh ph&iacute; lớn trong thời gian hạn hẹp v&agrave; kh&oacute; khăn n&agrave;y. Ch&uacute;ng t&ocirc;i tin rằng qu&yacute; nh&agrave; hảo t&acirc;m, qu&yacute; mạnh thường qu&acirc;n sẽ cùng nhau chung tay thực hiện chương trình th&agrave;nh c&ocirc;ng hơn nữa. Với tinh thần &ldquo;Sống l&agrave; sẻ chia&rdquo;, Hand In Hand mong nhận được nhiều tấm l&ograve;ng v&agrave;ng từ c&aacute;c MTQ.</p>\r\n\r\n<h3>54 hộ gia đ&igrave;nh v&agrave; ~100 em nhỏ cần c&oacute;:</h3>\r\n\r\n<h3>&ndash; Một c&acirc;y cầu d&agrave;i 15m: Tổng kinh ph&iacute; 54.514.550đ (C&oacute; đơn vị thi c&ocirc;ng chuy&ecirc;n l&agrave;m cầu tại v&ugrave;ng cao thực hiện)</h3>\r\n\r\n<h3>Th&ocirc;ng tin chi tiết l&agrave;m cầu:<a href=\"https://bit.ly/cautratap2022?fbclid=IwAR1ODPM6VMarzOvVFFzi9G4NQjqTDuWFAn8SiAUl6wkjN13KGnv2r1YL99M\">&nbsp;https://bit.ly/cautratap2022</a></h3>\r\n\r\n<h3>&ndash; 5 suất vật nu&ocirc;i cho 5 hộ gia đ&igrave;nh c&oacute; ho&agrave;n cảnh kh&oacute; khăn nhất.</h3>\r\n\r\n<h3>&ndash; Sửa 1 căn nh&agrave; anh Hồ Văn Vương &ndash; Gia đ&igrave;nh c&oacute; ho&agrave;n cảnh kh&oacute; khăn nhất.</h3>\r\n\r\n<h3>Trị gi&aacute;: 4.000.000đ</h3>\r\n\r\n<h3>Trẻ em + Học sinh cần c&oacute;:</h3>\r\n\r\n<h3>&ndash; 100 &aacute;o ấm cho học sinh + logo nh&oacute;m</h3>\r\n\r\n<h3>&ndash; Dụng cụ học tập: S&aacute;ch vở, b&uacute;t thước, truyện tranh.</h3>\r\n\r\n<h3>&ndash; 11 Bộ đồng phục cho c&aacute;c em từ lớp 2 &ndash; 12 c&oacute; th&agrave;nh t&iacute;ch tốt trong học tập.</h3>\r\n\r\n<h3>&ndash; 12 Cặp mầm non cho c&aacute;c b&eacute; mẫu gi&aacute;o c&oacute; th&agrave;nh t&iacute;ch &ldquo;B&eacute; ngoan&rdquo;</h3>\r\n\r\n<h3>&ndash; Dụng cụ y tế: 03 t&uacute;i y tế gồm: Băng c&aacute; nh&acirc;n, b&ocirc;ng băng, thuốc đỏ, oxy gi&agrave;, garo, băng keo y tế.</h3>\r\n\r\n<h3>&ndash; Dụng cụ giảng dạy: Phấn, dẻ lau bảng, khẩu trang, &aacute;o mưa, d&ugrave;.</h3>\r\n\r\n<h3>Phương tiện di chuyển nh&oacute;m cần hỗ trợ:</h3>\r\n\r\n<h3>&ndash; 02 xe 45 chỗ.</h3>\r\n\r\n<h3>&ndash; 01 xe h&agrave;ng 05~7 tấn.</h3>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2022/08/Cau-cau-1024x683.jpg\" style=\"height:680px; width:1020px\" /></p>\r\n\r\n<p>M&ocirc; phỏng nơi l&agrave;m cầu</p>', 'Qua phóng sự do nhóm Từ thiện Hand In Hand Việt Hàn thực hiện vào ngày 09/07/2022 – 10/07/2022, ta có thể thấy: cái gọi là nhà để ở chỉ được ghép bằng những miếng gỗ không chắc chắn, thậm chí không đủ che mưa che nắng và dễ dàng bị cuốn trôi khi có trận bão lớn kéo về. ', 100000000, 0, 'Nhóm từ thiện Hand In Hand Việt - Hàn', '0343428511', 'https://www.facebook.com/Tuthien.HandInHandVietHan', 1661014800, 100000000, 1, 1, NULL, '2023-06-09 12:29:33', '2023-06-09 17:29:53', NULL),
(8, 1, 2, 'Trà Giáp Vẽ Nụ Cười Vùng Cao', 'tra-giap-ve-nu-cuoi-vung-cao', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686314111/project/tra-giap-ve-nu-cuoi-vung-cao-5251.jpg', '<p dir=\"auto\">Hiện tại Th&ocirc;n 3, x&atilde; Tr&agrave; Gi&aacute;p nơi ch&uacute;ng t&ocirc;i sắp đến v&agrave;o ng&agrave;y 30/04/2021 &ndash; 02/05/2021 c&oacute; 260 hộ d&acirc;n đều l&agrave; người đồng b&agrave;o Ca Dong , với 100% l&agrave; hộ ngh&egrave;o. Cả Th&ocirc;n c&oacute; rất nhiều trẻ em, nhưng chỉ c&oacute; 04 điểm trường, 01 KTX, to&agrave;n bộ th&ocirc; sơ v&agrave; hư hỏng, hầu như kh&ocirc;ng c&oacute; khu vui chơi d&agrave;nh ri&ecirc;ng cho c&aacute;c em nhỏ. Nh&acirc;n đ&acirc;y, nh&oacute;m mạnh dạn đề xuất những c&ocirc;ng t&aacute;c thực hiện tr&ecirc;n Tr&agrave; Gi&aacute;p:</p>\r\n\r\n<p dir=\"auto\">&ndash; Cải thiện 04 trường học (02 trường học tiểu học + 02 trường mầm non) + 01 KTX.</p>\r\n\r\n<p dir=\"auto\">&ndash; L&agrave;m khu vui chơi cho trẻ em tại trường mầm non</p>\r\n\r\n<p dir=\"auto\">&ndash; Cắt t&oacute;c cho đồng b&agrave;o v&agrave; học sinh Cadong.</p>\r\n\r\n<p dir=\"auto\">&ndash; L&agrave;m bảng t&ecirc;n trường tiểu học v&agrave; cổng ch&agrave;o.</p>\r\n\r\n<p dir=\"auto\">&ndash; Sơn lại trường tiểu học v&agrave; cải thiện trường mầm non bị tốc m&aacute;i do b&atilde;o số 09.</p>\r\n\r\n<p dir=\"auto\">&ndash; Trồng hoa v&agrave; tu sửa lớp học.</p>\r\n\r\n<p dir=\"auto\">&ndash; Trồng lại cột cờ cho trường.</p>\r\n\r\n<p dir=\"auto\">&ndash; X&acirc;y dựng tủ truyện cho học sinh v&agrave; tủ s&aacute;ch thư viện.</p>\r\n\r\n<p dir=\"auto\">&ndash; Cung cấp dụng cụ v&agrave; vật phẩm y tế, dụng cụ c&aacute; nh&acirc;n hỗ trợ gi&aacute;o vi&ecirc;n dạy học.</p>\r\n\r\n<p dir=\"auto\">&ndash; Chạy hệ thống chiếu s&aacute;ng.</p>\r\n\r\n<p dir=\"auto\">&ndash; L&agrave;m s&acirc;n b&oacute;ng đ&aacute; v&agrave; b&oacute;ng chuyền cho học sinh.</p>\r\n\r\n<p dir=\"auto\">&ndash; Dạy học cho học sinh: Đ&aacute;nh răng, an to&agrave;n giao th&ocirc;ng, rửa mặt, rửa tay, đeo khẩu trang, ph&ograve;ng chống dịch bệnh.</p>\r\n\r\n<p dir=\"auto\">&ndash; Tổ chức ph&aacute;t qu&agrave; nhu phẩm thiết yếu: &aacute;o quần cũ, m&igrave; t&ocirc;m, dầu ăn, mắm, muối, &hellip;</p>\r\n\r\n<p dir=\"auto\">&ndash; Lửa trại giao lưu đồng b&agrave;o Cadong với nh&oacute;m Hand In Hand tại địa phương Tr&agrave; Gi&aacute;p.</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2021/03/29-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p>Trẻ em đến trường phải băng qua những c&aacute;nh rừng</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2021/03/31-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p>Ng&ocirc;i trường cũ n&aacute;t, hư hỏng nhiều</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2021/03/37-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p>&ldquo;Em ước c&oacute; một tr&aacute;i b&oacute;ng&rdquo;</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2021/03/40-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p>Những đứa trẻ hồn nhi&ecirc;n nơi đ&acirc;y</p>\r\n\r\n<p><img alt=\"\" src=\"http://handinhandviethan.com/wp-content/uploads/2021/03/38-1024x576.jpg\" style=\"height:574px; width:1020px\" /></p>\r\n\r\n<p dir=\"auto\">Để thực hiện h&oacute;a được đ&uacute;ng tiến độ v&agrave; mục ti&ecirc;u đề ra cho chương tr&igrave;nh Tr&agrave; Gi&aacute;p &ndash; Vẽ nụ cười v&ugrave;ng cao, Nh&oacute;m từ thiện Hand In Hand Việt H&agrave;n sẽ cần một số lượng kinh ph&iacute; lớn trong thời gian hạn hẹp v&agrave; kh&oacute; khăn n&agrave;y (V&igrave; t&igrave;nh h&igrave;nh dịch bệnh tại Việt Nam). Ch&uacute;ng t&ocirc;i tin rằng qu&yacute; nh&agrave; hảo t&acirc;m, qu&yacute; mạnh thường qu&acirc;n sẽ cùng nhau chung tay thực hiện chương trình th&agrave;nh c&ocirc;ng hơn nữa. Với tinh thần &ldquo; Sống l&agrave; sẻ chia&rdquo;, Hand In Hand mong nhận được nhiều tấm l&ograve;ng v&agrave;ng từ c&aacute;c MTQ.</p>\r\n\r\n<p dir=\"auto\">260 HỘ GIA Đ&Igrave;NH V&Agrave; 04 ĐIỂM TRƯỜNG, 01 KTX CỤ THỂ NHƯ SAU:</p>\r\n\r\n<p dir=\"auto\"><img alt=\"‼️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t77/1/16/203c.png\" style=\"height:16px; width:16px\" />&nbsp;260 hộ, mỗi hộ cần c&oacute;:</p>\r\n\r\n<p dir=\"auto\">&ndash; 260 (hộ) x 03 th&ugrave;ng m&igrave; t&ocirc;m x = 780 th&ugrave;ng.</p>\r\n\r\n<p dir=\"auto\">&ndash; 260 (hộ) x 01 bao quần &aacute;o (được mix) = 260 bao.</p>\r\n\r\n<p dir=\"auto\">&ndash; 260 (hộ) x 01 dầu ăn 1L = 260 chai.</p>\r\n\r\n<p dir=\"auto\">&ndash; 260 (hộ) x 01 mắm 1L = 260 chai.</p>\r\n\r\n<p dir=\"auto\">&ndash; 260 (hộ) x 03 bao muối 500g = 780 bao.</p>\r\n\r\n<p dir=\"auto\">&ndash; 260 (hộ) x 02 bao đường 500g = 520 bao.</p>\r\n\r\n<p dir=\"auto\"><img alt=\"‼️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t77/1/16/203c.png\" style=\"height:16px; width:16px\" />&nbsp;Học sinh + trường học cần c&oacute;:</p>\r\n\r\n<p dir=\"auto\">&ndash; 70 &aacute;o ấm cho học sinh + logo nh&oacute;m</p>\r\n\r\n<p dir=\"auto\">&ndash; Dụng cụ học tập: S&aacute;ch vở, b&uacute;t thước, truyện tranh, 02 tủ truyện, 01 tủ s&aacute;ch thư viện.</p>\r\n\r\n<p dir=\"auto\">&ndash; Dụng cụ y tế: 03 t&uacute;i y tế gồm: Băng c&aacute; nh&acirc;n, b&ocirc;ng băng, thuốc đỏ, oxy gi&agrave;, garo, băng keo y tế.</p>\r\n\r\n<p dir=\"auto\">&ndash; Dụng cụ giảng dạy: Phấn, dẻ lau bảng, khẩu trang, &aacute;o mưa, d&ugrave;.</p>\r\n\r\n<p dir=\"auto\">&ndash; Dụng cụ vệ sinh: 05 th&ugrave;ng r&aacute;c lớn, 03kg t&uacute;i ni l&ocirc;ng đựng r&aacute;c tiện lợi, 02 cuộn d&acirc;y th&eacute;p cố định.</p>\r\n\r\n<p dir=\"auto\">&ndash; Dụng cụ sơn sửa trường học: Đinh th&eacute;p 5, 6; 05 cuộn d&acirc;y th&eacute;p lớn, 01 bao xi măng, giấy nh&aacute;m, 05 ch&agrave; sắt, 02 chổi nhựa.</p>\r\n\r\n<p dir=\"auto\">o Dụng cụ l&agrave;m khu vui chơi trẻ em: 10m d&acirc;y thừng, 02 lốp xe, 01 quả b&oacute;ng chuyền, 01 quả banh, 02 lưới cầu g&ocirc;n đ&aacute; banh, 01 lưới b&oacute;ng chuyền mini.</p>\r\n\r\n<p dir=\"auto\">o Dụng cụ sơn tường 02 trường học: 03 bộ con lăn sơn tường, 02 th&ugrave;ng sơn nước, 01 bộ sơn vẽ cho trường mầm non.</p>\r\n\r\n<p dir=\"auto\">&ndash; L&agrave;m bảng t&ecirc;n trường inox với k&iacute;ch thước: 03m x 30cm.</p>\r\n\r\n<p dir=\"auto\">&ndash; Cổng ch&agrave;o trường tiểu học: 1,5m x 20cm</p>\r\n\r\n<p dir=\"auto\">&ndash; Bảng th&ocirc;ng b&aacute;o: 02 bảng 80x60cm.</p>\r\n\r\n<p dir=\"auto\">&ndash; Hệ thống điện: 70m d&acirc;y điện loại 01, 05 đ&egrave;n led bulb b&oacute;ng tr&ograve;n 55W, 05 ổ điện 03 ch&acirc;n, 15 ph&iacute;ch cắm, 05 ph&iacute;ch &acirc;m, d&acirc;y r&uacute;t.</p>\r\n\r\n<p dir=\"auto\">&ndash; 03 bộ cắt t&oacute;c.</p>\r\n\r\n<p dir=\"auto\"><img alt=\"‼️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t77/1/16/203c.png\" style=\"height:16px; width:16px\" />&nbsp;Phương tiện di chuyển nh&oacute;m cần hỗ trợ:</p>\r\n\r\n<p dir=\"auto\">&ndash; 02 xe 45 chỗ.</p>\r\n\r\n<p dir=\"auto\">&ndash; 01 xe h&agrave;ng 07~10 tấn.</p>', 'Hiện tại Thôn 3, xã Trà Giáp nơi chúng tôi sắp đến vào ngày 30/04/2021 – 02/05/2021 có 260 hộ dân đều là người đồng bào Ca Dong , với 100% là hộ nghèo. Cả Thôn có rất nhiều trẻ em, nhưng chỉ có 04 điểm trường, 01 KTX, toàn bộ thô sơ và hư hỏng, hầu như không có khu vui chơi dành riêng cho các em nhỏ.', 50000000, 0, 'Nhóm từ thiện Hand In Hand Việt - Hàn', '0343428511', 'https://www.facebook.com/Tuthien.HandInHandVietHan', 1619629200, 50000000, 1, 1, NULL, '2023-06-09 12:35:12', '2023-06-09 12:36:22', NULL),
(9, 1, 2, 'CHUNG TAY CÙNG BÀ CON TUYẾN KÊNH PHÈN', 'chung-tay-cung-ba-con-tuyen-kenh-phen', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686318787/project/chung-tay-cung-ba-con-tuyen-kenh-phen-4733.jpg', '<p>Tại&nbsp;<strong>tuyến k&ecirc;nh Ph&egrave;n, x&atilde; Vĩnh Viễn, Long Mỹ, Hậu Giang</strong>&nbsp;c&oacute; 1 đoạn đường chỉ d&agrave;i 1km đi v&agrave;o nơi sinh sống của khoảng 30 hộ d&acirc;n nhưng mấy chục năm l&uacute;c n&agrave;o cũng trong t&igrave;nh trạng lầy lội khiến việc đi lại của người d&acirc;n v&ocirc; c&ugrave;ng vất vả. Đ&atilde; gần 50 năm từ ng&agrave;y b&agrave; con khai hoang ra v&ugrave;ng đất n&agrave;y, một con đường b&ecirc; t&ocirc;ng mong mỏi qua bao thế hệ vẫn chưa được ho&agrave;n th&agrave;nh do kinh tế kh&oacute; khăn.</p>\r\n\r\n<p>V&agrave;o m&ugrave;a mưa, đường lu&ocirc;n nh&atilde;o nhoẹt, b&ugrave;n lầy v&agrave; hết sức trơn. Để lưu th&ocirc;ng, b&agrave; con phải lội bộ ra đường, xin rửa ch&acirc;n hoặc c&oacute; khi phải thay đồ nhờ ở nh&agrave; một người d&acirc;n rồi mới đi học, đi l&agrave;m. Những em b&eacute; nhỏ th&igrave; ba mẹ phải đưa đi, ảnh hưởng rất nhiều đến c&ocirc;ng việc đồng &aacute;n.</p>\r\n\r\n<p><img alt=\"3\" src=\"https://quybongsen.org/wp-content/uploads/2018/09/3-1.jpg\" style=\"height:893px; width:960px\" /></p>\r\n\r\n<p><img alt=\"2\" src=\"https://quybongsen.org/wp-content/uploads/2018/09/2.jpg\" style=\"height:1280px; width:960px\" /></p>\r\n\r\n<p><img alt=\"123\" src=\"https://quybongsen.org/wp-content/uploads/2018/09/123.jpg\" style=\"height:1280px; width:915px\" /></p>\r\n\r\n<hr />\r\n<p>Tổng chi ph&iacute; dự kiến cho quảng đường b&ecirc; t&ocirc;ng d&agrave;i gần 1km, rộng 1,2m n&agrave;y khoảng 60 triệu đồng, b&agrave; con trong ấp đ&atilde; gom g&oacute;p được hơn 1 nửa, c&ograve;n hơn 20 triệu quỹ từ thiện B&ocirc;ng Sen sẽ k&ecirc;u gọi quy&ecirc;n g&oacute;p từ nay đến hết ng&agrave;y 31/10/2018, hy vọng c&oacute; thể đủ tiền để c&ugrave;ng b&agrave; con l&agrave;m 1 con đường khang trang để giảm bớt những kh&oacute; khăn của b&agrave; con nơi đ&acirc;y, gi&uacute;p c&aacute;c em nhỏ c&oacute; thể tự đến trường, kh&ocirc;ng phải dỡ dang việc học chỉ v&igrave; đường x&aacute; cản trở.</p>\r\n\r\n<hr />\r\n<p><img alt=\"1\" src=\"https://quybongsen.org/wp-content/uploads/2018/09/1.jpg\" style=\"height:960px; width:1280px\" /></p>\r\n\r\n<hr />\r\n<p>Mọi đ&oacute;ng g&oacute;p&nbsp;cho&nbsp;Con đường tại tuyến k&ecirc;nh Ph&egrave;n &ndash; Hậu Giang qu&yacute; nh&agrave; hảo t&acirc;m&nbsp;vui l&ograve;ng li&ecirc;n hệ đến Quỹ Từ Thiện B&ocirc;ng Sen &ndash; Lầu 5 số 7- 9 -11 Mai Thị Lựu, P.Đa Kao, Q1, TP.HCM &ndash; ĐT: (028) 39107612/ Số t&agrave;i khoản VNĐ: 6420201012822 &ndash; Ng&acirc;n h&agrave;ng N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n (Agribank) &ndash; Chi nh&aacute;nh 10 hoặc số TK: 0371000481127 &ndash; Ng&acirc;n h&agrave;ng Thương mại Cổ phần Ngoại thương Việt Nam (Vietcombank) &ndash; Chi nh&aacute;nh T&acirc;n Định, hoặc Số TK: 508317 &ndash; Ng&acirc;n h&agrave;ng Thương mại Cổ phần &Aacute; Ch&acirc;u (ACB) &ndash; Chi Nh&aacute;nh S&agrave;i G&ograve;n, hoặc số TK: 18610000165426 &ndash; Ng&acirc;n h&agrave;ng Đầu tư v&agrave; Ph&aacute;t triển Việt Nam (BIDV) &ndash; Chi nh&aacute;nh H&agrave;m Nghi.</p>\r\n\r\n<hr />\r\n<p><strong>Cập nhật:</strong>&nbsp;Đến ng&agrave;y 25/09/2018 Dự &aacute;n &ldquo;Chung tay c&ugrave;ng b&agrave; con tuyến k&ecirc;nh Ph&egrave;n, x&atilde; Vĩnh Viễn, Long Mỹ, Hậu Giang x&acirc;y con đường mong ước&rdquo; đ&atilde; vận động đủ số tiền c&ograve;n thiếu để triển khai dự &aacute;n. Quỹ sẽ ngưng nhận đ&oacute;ng g&oacute;p cho dự &aacute;n n&agrave;y. Ch&uacute;ng t&ocirc;i sẽ cập nhật th&ocirc;ng tin, h&igrave;nh ảnh sau khi ho&agrave;n th&agrave;nh dự &aacute;n. Dự kiến dự &aacute;n sẽ triển khai v&agrave;o th&aacute;ng 11/2018, sau khi hết m&ugrave;a mưa. Xin c&aacute;m ơn c&aacute;c nh&agrave; hảo t&acirc;m đ&atilde; chung tay đ&oacute;ng g&oacute;p:</p>\r\n\r\n<p>1. Nh&agrave; hảo t&acirc;m Tran Minh Diem Phuc ủng hộ 10.000.000 đồng</p>\r\n\r\n<p>2. Nh&agrave; hảo t&acirc;m Phước Lộc &ndash; H&ugrave;ng Huỳnh ủng hộ 5.000.000 đồng</p>\r\n\r\n<p>3. Nh&agrave; hảo t&acirc;m Nguyễn Thị Tường Lan ủng hộ 2.000.000 đồng</p>\r\n\r\n<p>4. Nh&agrave; hảo t&acirc;m Vo Dinh Nhat Huy ủng hộ 2.000.000 đồng</p>\r\n\r\n<p>5. Nh&agrave; hảo t&acirc;m Nguyễn Th&ugrave;y Trang ủng hộ 3.000.000 đồng</p>\r\n\r\n<p>6. Nh&agrave; hảo t&acirc;m Phan Minh Rang ủng hộ 250.000 đồng</p>\r\n\r\n<p>7. Nh&agrave; hảo t&acirc;m Do Nu Hoang Oanh ủng hộ 200.000 đồng.</p>\r\n\r\n<hr />\r\n<p><strong>Cập nhật</strong>: Cuối c&ugrave;ng con đường mong ước ở tuyến k&ecirc;nh Ph&egrave;n đ&atilde; được thi c&ocirc;ng những đoạn đầu ti&ecirc;n. Điều đ&aacute;ng mừng nhờ sự lan tỏa từ Quỹ từ Thiện B&ocirc;ng Sen, sự đ&oacute;ng g&oacute;p ủng hộ từ mạnh thường qu&acirc;n gần xa, Ủy ban Nh&acirc;n D&acirc;n X&atilde; đ&atilde; xin th&ecirc;m kinh ph&iacute; hỗ trợ mở rộng con đường từ 1.2 m&eacute;t như ban đầu l&ecirc;n 2 m&eacute;t.</p>\r\n\r\n<p><img alt=\"2\" src=\"https://quybongsen.org/wp-content/uploads/2018/09/2-1.jpg\" style=\"height:1280px; width:720px\" /></p>', 'Tại tuyến kênh Phèn, xã Vĩnh Viễn, Long Mỹ, Hậu Giang có 1 đoạn đường chỉ dài 1km đi vào nơi sinh sống của khoảng 30 hộ dân nhưng mấy chục năm lúc nào cũng trong tình trạng lầy lội khiến việc đi lại của người dân vô cùng vất vả. Đã gần 50 năm từ ngày bà con khai hoang ra vùng đất này, một con đường bê tông mong mỏi qua bao thế hệ vẫn chưa được hoàn thành do kinh tế khó khăn.', 22000000, 100000, 'Người dân tuyến kênh Phèn, xã Vĩnh Viễn, Long Mỹ, Hậu Giang', '0964114749', NULL, 1702054800, 0, 0, 1, NULL, '2023-06-09 12:40:29', '2023-06-13 16:08:21', NULL),
(10, 2, 2, 'Nụ cười nắng hạ', 'nu-cuoi-nang-ha', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686409877/project/nu-cuoi-nang-ha-4567.jpg', '<p><img alt=\"💙\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/1f499.png\" style=\"height:16px; width:16px\" />NỤ CƯỜI NẮNG HẠ - 31.05.2023<img alt=\"💙\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/1f499.png\" style=\"height:16px; width:16px\" /></p>\r\n\r\n<p><img alt=\"💫\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ted/1/16/1f4ab.png\" style=\"height:16px; width:16px\" />Đ&atilde; một tuần tr&ocirc;i qua, nhắc chắc hẳn những cảm x&uacute;c ng&agrave;y h&ocirc;m đ&oacute; vẫn vẹn nguy&ecirc;n trong tr&aacute;i tim của mỗi th&agrave;nh vi&ecirc;n tham gia chương tr&igrave;nh &ldquo;Nụ cười nắng hạ&rdquo;&hellip;</p>\r\n\r\n<p><img alt=\"💫\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ted/1/16/1f4ab.png\" style=\"height:16px; width:16px\" />Được sinh ra v&agrave; lớn l&ecirc;n trong v&ograve;ng tay y&ecirc;u thương của bố v&agrave; mẹ, được hưởng một cuộc sống đủ đầy, trải qua tuổi thơ &ecirc;m đềm, hạnh ph&uacute;c. Điều b&igrave;nh thường với tất cả ch&uacute;ng ta nhưng với c&aacute;c em nhỏ ở &quot;Trung t&acirc;m bảo trợ trẻ em đường phố Đ&agrave; Nẵng&quot; lại l&agrave; điều ước kh&oacute; c&oacute; thể trở th&agrave;nh hiện thực.</p>\r\n\r\n<p><img alt=\"💫\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ted/1/16/1f4ab.png\" style=\"height:16px; width:16px\" />C&aacute;c em nhỏ ở đ&acirc;y đa phần l&agrave; những em b&eacute; mất bố mẹ, trẻ em cơ nhỡ. C&oacute; những em may mắn hơn một ch&uacute;t, c&ograve;n bố mẹ nhưng điều kiện sống lại v&ocirc; c&ugrave;ng kh&oacute; khăn, thiếu thốn, kh&ocirc;ng đủ điều kiện để cho con được đến trường.</p>\r\n\r\n<p>&quot;Trung t&acirc;m bảo trợ trẻ em đường phố Đ&agrave; Nẵng&quot; l&agrave; ng&ocirc;i nh&agrave; thứ hai cho những tr&aacute;i tim b&eacute; nhỏ ấy, mở ra cho c&aacute;c em một c&aacute;nh cửa mới với đầy ắp t&igrave;nh y&ecirc;u thương v&agrave; một cuộc sống được đảm bảo.</p>\r\n\r\n<p><img alt=\"💫\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ted/1/16/1f4ab.png\" style=\"height:16px; width:16px\" />Với hi vọng sưởi ấm những tr&aacute;i tim nhỏ b&eacute; đầy vết thương, ng&agrave;y 31/05 vừa qua, Nh&oacute;m từ thiện Hand In Hand Việt H&agrave;n đ&atilde; gh&eacute; thăm v&agrave; tổ chức chương tr&igrave;nh &quot;&quot;Nụ cười nắng hạ&quot;&quot; cho c&aacute;c em nhỏ ở đ&acirc;y.</p>\r\n\r\n<p><img alt=\"💫\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ted/1/16/1f4ab.png\" style=\"height:16px; width:16px\" />Ng&agrave;y h&ocirc;m đ&oacute;, ngo&agrave;i việc được tham gia v&agrave;o những tr&ograve; chơi đầy l&iacute; th&uacute;, c&aacute;c em nhỏ c&ograve;n được xem những vở kịch do c&aacute;c anh chị th&agrave;nh vi&ecirc;n Hand In Hand biểu diễn, được giao lưu văn nghệ, c&ugrave;ng nhau tạo n&ecirc;n những chiếc b&aacute;nh đầy dễ thương&hellip;</p>\r\n\r\n<p><img alt=\"💫\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/ted/1/16/1f4ab.png\" style=\"height:16px; width:16px\" />Hi vọng m&oacute;n qu&agrave; m&agrave; HIH d&agrave;nh tặng c&aacute;c em v&agrave;o ng&agrave;y quốc tế thiếu nhi n&agrave;y sẽ an ủi được phần n&agrave;o những tr&aacute;i tim đầy vết xước ấy. Mong rằng nụ cười vẫn m&atilde;i lu&ocirc;n hiện diện tr&ecirc;n gương mặt c&aacute;c em. Mong rằng c&aacute;c em vẫn sẽ l&agrave; những c&ocirc; b&eacute;, cậu b&eacute; lạc quan, y&ecirc;u đời, vượt qua nghịch cảnh v&agrave; sải c&aacute;nh tung bay tr&ecirc;n cuộc đời d&agrave;i rộng n&agrave;y!</p>', 'Đã một tuần trôi qua, nhắc chắc hẳn những cảm xúc ngày hôm đó vẫn vẹn nguyên trong trái tim của mỗi thành viên tham gia chương trình “Nụ cười nắng hạ”…', 2500000, 0, 'Nhóm từ thiện Hand In Hand Việt - Hàn', '0343428511', 'https://www.facebook.com/Tuthien.HandInHandVietHan', 1685552400, 2500000, 1, 1, NULL, '2023-06-10 14:46:57', '2023-06-12 19:25:33', NULL),
(11, 2, 2, 'Giúp trẻ mồ côi tại mái ấm', 'giup-tre-mo-coi-tai-mai-am', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686409930/project/giup-tre-mo-coi-tai-mai-am-8597.jpg', '<p>Th&ecirc;m một dự &aacute;n kh&ocirc;ng thời hạn nằm trong chương tr&igrave;nh hỗ trợ gi&aacute;o dục trẻ em tỉnh Quảng Trị. Mỗi th&aacute;ng, Qũy B&ocirc;ng Sen sẽ cần vận động 8 triệu đồng hỗ trợ sữa cho M&aacute;i Ấm L&acirc;m B&iacute;ch ở Đ&ocirc;ng H&agrave; v&agrave; 15 triệu cho M&aacute;i Ấm Cam Lộ để hỗ trợ sữa v&agrave; thực phẩm cho những trẻ mồ c&ocirc;i đang được nu&ocirc;i dưỡng tại đ&acirc;y.</p>\r\n\r\n<p dir=\"auto\">Hậu Covid</p>\r\n\r\n<p dir=\"auto\">Tr&ecirc;n mỗi người bệnh c&oacute; thể để lại v&agrave;i di chứng, nhưng tr&ecirc;n nền kinh tế của cả nước, dấu t&iacute;ch Covid để lại cho nền kinh tế qu&aacute; nặng nề.</p>\r\n\r\n<p>Chuyến khảo s&aacute;t miền Trung th&aacute;ng 7 vừa qua, ch&uacute;ng t&ocirc;i ghi nhận phần lớn người d&acirc;n ngh&egrave;o hơn hẳn so với trước đ&acirc;y. C&oacute; thể c&aacute;c khu du lịch, c&aacute;c tụ điểm vui chơi kh&ocirc;ng thấy biểu hiện của sức mua suy giảm bởi một số thị d&acirc;n kh&ocirc;ng bị mất việc, c&ograve;n phần lớn người lao động ngh&egrave;o về qu&ecirc;, cuộc mưu sinh trở n&ecirc;n kh&oacute; khăn hơn trước, gi&aacute; cả leo thang ch&oacute;ng mặt.Ở những cơ sở từ thiện x&atilde; hội tại c&aacute;c tỉnh ngh&egrave;o như Quảng Trị, Gia Lai v.v&hellip; cuộc sống v&ocirc; c&ugrave;ng kh&oacute; khăn. Người đến thăm viếng v&agrave; tặng qu&agrave; &iacute;t đi hẳn, giảm s&uacute;t rất nhiều khiến nguồn thu kh&ocirc;ng c&oacute;. C&aacute;c ma sơ chạy vạy đủ c&aacute;ch, chế biến tr&agrave; gừng, bột ngũ cốc b&aacute;n v&agrave;i chục ng&agrave;n mong cải thiện bữa ăn cho c&aacute;c ch&aacute;u mồ c&ocirc;i hoặc người d&acirc;n tộc thất học.</p>\r\n\r\n<p dir=\"auto\">C&agrave;ng xa trung t&acirc;m, c&agrave;ng &iacute;t người đến th&igrave; c&agrave;ng kh&oacute; khăn. M&aacute;i ấm Cam Lộ ở Quảng Trị, nơi Qu&aacute;n Y&ecirc;n Vui trợ gi&uacute;p thực phẩm khoảng 9 triệu mỗi th&aacute;ng cho 32 ch&aacute;u, c&aacute;c ma sơ cũng rất chật vật t&igrave;m th&ecirc;m nguồn n&agrave;y nguồn kia cho c&aacute;c em. Cả đo&agrave;n ch&uacute;ng t&ocirc;i v&ocirc; c&ugrave;ng x&uacute;c động khi sơ HĐ tr&ograve;n mắt, ngẩn người v&igrave; ngạc nhi&ecirc;n vỡ &ograve;a niềm vui khi nghe ch&uacute;ng t&ocirc;i ngỏ lời gi&uacute;p th&ecirc;m 6 triệu nữa mỗi th&aacute;ng để c&aacute;c ch&aacute;u c&oacute; thể uống sữa. Sữa! Ở nơi xa x&ocirc;i n&agrave;y trẻ con trong M&aacute;i ấm từ thiện được uống sữa l&agrave; điều qu&aacute; hoang đường.</p>\r\n\r\n<p>Vậy l&agrave; th&ecirc;m một dự &aacute;n kh&ocirc;ng thời hạn nằm trong chương tr&igrave;nh hỗ trợ gi&aacute;o dục trẻ em tỉnh Quảng Trị. Mỗi th&aacute;ng sẽ cần vận động 8 triệu đồng cho M&aacute;i Ấm L&acirc;m B&iacute;ch ở Đ&ocirc;ng H&agrave; v&agrave; 15 triệu đồng cho M&aacute;i Ấm Cam Lộ. Ch&uacute;ng t&ocirc;i chỉ l&agrave; người giao nhận, l&agrave; shipper vận chuyển l&ograve;ng tốt của qu&yacute; nh&agrave; hảo t&acirc;m đến những nơi cần thiết. Nhận một lời c&aacute;m ơn từ c&aacute;c ma sơ cũng đ&atilde; l&agrave; qu&aacute; ngại ng&ugrave;ng.</p>\r\n\r\n<p dir=\"auto\">lvc, trong nh&oacute;m chăm s&oacute;c QBS</p>\r\n\r\n<p>Mọi sự đồng h&agrave;nh c&ugrave;ng dự &aacute;n c&oacute; thể gửi về:</p>\r\n\r\n<p>T&ecirc;n T&agrave;i Khoản: Quỹ Từ Thiện B&ocirc;ng Sen</p>\r\n\r\n<p>Nội dung:&nbsp;<strong>T&ecirc;n Nh&agrave; hảo t&acirc;m + ủng hộ Dự &aacute;n trẻ mồ c&ocirc;i M&aacute;i ấm</strong></p>\r\n\r\n<p>Một số h&igrave;nh ảnh sinh hoạt tại 2 m&aacute;i ấm m&agrave; ch&uacute;ng t&ocirc;i ghi nhận được</p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/292556345_5640591772641092_6067964686078809376_n.jpg\" style=\"height:1276px; width:956px\" /></p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/292676111_5640595325974070_2247929270877854814_n.jpg\" style=\"height:956px; width:1276px\" /></p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/293424105_5640594029307533_5920115371596682741_n.jpg\" style=\"height:1280px; width:960px\" /></p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/293487657_5640597452640524_7705078184158091565_n.jpg\" style=\"height:1600px; width:1200px\" /></p>', 'Thêm một dự án không thời hạn nằm trong chương trình hỗ trợ giáo dục trẻ em tỉnh Quảng Trị. Mỗi tháng, Qũy Bông Sen sẽ cần vận động 8 triệu đồng hỗ trợ sữa cho Mái Ấm Lâm Bích ở Đông Hà và 15 triệu cho Mái Ấm Cam Lộ để hỗ trợ sữa và thực phẩm cho những trẻ mồ côi đang được nuôi dưỡng tại đây.', 10000000, 0, 'Mái Ấm Cam Lộ', '0946234470', NULL, 1689699600, 3500000, 0, 1, NULL, '2023-06-10 14:48:59', '2023-06-11 16:49:45', NULL);
INSERT INTO `projects` (`id`, `id_category`, `id_user_create`, `name_project`, `slug`, `thumbnail`, `description`, `short_desc`, `target`, `donation`, `donate_for`, `phone`, `link_fb`, `date_action`, `fund`, `status`, `active`, `finished_at`, `created_at`, `updated_at`, `deleted_at`) VALUES
(12, 4, 2, 'Clean Up Sơn Trà', 'clean-up-son-tra', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686666311/project/clean-up-son-tra-1319.jpg', '<p>[CLEAN UP SON TRA 2021 &ndash; V&Igrave; MỘT SƠN TR&Agrave; XANH]</p>\r\n\r\n<p><img alt=\"❤️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/2764.png\" style=\"height:16px; width:16px\" /> Hưởng ứng chương tr&igrave;nh &ldquo;Clean up Son Tra &ndash; v&igrave; một Sơn Tr&agrave; xanh&rdquo; v&agrave;o s&aacute;ng Chủ nhật, ng&agrave;y 29/03/2021 tổ chức bởi Ban Quản l&yacute; b&aacute;n đảo Sơn Tr&agrave; v&agrave; c&aacute;c b&atilde;i biển, Nh&oacute;m từ thiện Hand In Hand Việt H&agrave;n đ&atilde; huy động được đ&ocirc;ng đảo th&agrave;nh vi&ecirc;n tham gia. G&oacute;p sức mang đến một Sơn Tr&agrave; xanh v&agrave; kh&ocirc;ng r&aacute;c thải nhựa.</p>\r\n\r\n<p><img alt=\"❤️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/2764.png\" style=\"height:16px; width:16px\" /> V&igrave; địa điểm tập kết c&aacute;ch xa điểm xuất ph&aacute;t của nh&oacute;m, n&ecirc;n th&agrave;nh vi&ecirc;n đ&atilde; phải thức dậy từ rất sớm để kịp di chuyển l&uacute;c 5h10 s&aacute;ng.</p>\r\n\r\n<p><img alt=\"❤️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/2764.png\" style=\"height:16px; width:16px\" /> Nhận được sự tin tưởng từ ban tổ chức, Hand In Hand nhận nhiệm vụ điều phối giao th&ocirc;ng v&agrave; sắp xếp xe cho những đơn vị đến tham gia chiến dịch. Với sự đổ về của gần 400 người thuộc 9 đơn vị trong th&agrave;nh phố Đ&agrave; Nẵng, lượng xe cộ v&ocirc; c&ugrave;ng đ&ocirc;ng đ&uacute;c. Nhưng nhờ tinh thần nghi&ecirc;m t&uacute;c v&agrave; hăng h&aacute;i, nh&oacute;m đ&atilde; hỗ trợ chương tr&igrave;nh diễn ra su&ocirc;n sẻ tr&aacute;nh khỏi t&igrave;nh trạng &ugrave;n tắc, lộn xộn.</p>\r\n\r\n<p><img alt=\"❤️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/2764.png\" style=\"height:16px; width:16px\" /> Sau khi tập kết, nh&oacute;m di chuyển nhặt r&aacute;c theo tuyến đường từ ch&ugrave;a Linh Ứng đến Intercon. Với số lượng hơn 30 th&agrave;nh vi&ecirc;n tham gia, Hand In Hand đ&atilde; nhanh ch&oacute;ng dọn sạch tuyến đường được giao. Mặc d&ugrave; gặp nhiều kh&oacute; khăn v&igrave; r&aacute;c thải nằm trong những bụi rậm, cống nước, v&aacute;ch đ&aacute;, nhưng th&agrave;nh vi&ecirc;n nh&oacute;m đ&atilde; kh&ocirc;ng quản ngại kh&oacute; khăn, quyết t&acirc;m kh&ocirc;ng để s&oacute;t lại r&aacute;c tr&ecirc;n b&aacute;n đảo Sơn Tr&agrave;.</p>\r\n\r\n<p><img alt=\"❤️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/2764.png\" style=\"height:16px; width:16px\" /> Qua chiến dịch lần n&agrave;y, Hand In Hand cũng như ban tổ chức muốn lan tỏa th&ocirc;ng điệp, n&acirc;ng cao nhận thức của cộng đồng về bảo vệ m&ocirc;i trường, vận động người d&acirc;n v&agrave; du kh&aacute;ch tham gia c&aacute;c hoạt động, phong tr&agrave;o bảo vệ m&ocirc;i trường, bảo tồn đa dạng sinh học v&agrave; bảo vệ dộng vật hoang d&atilde; tại b&aacute;n đảo Sơn Tr&agrave;.</p>\r\n\r\n<p><img alt=\"❤️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/2764.png\" style=\"height:16px; width:16px\" /> Cuối c&ugrave;ng, Hand In Hand xin gửi lời cảm ơn đến Ban Quản l&yacute; b&aacute;n đảo Sơn Tr&agrave; v&agrave; c&aacute;c b&atilde;i biển đ&atilde; tổ chức một chiến dịch v&igrave; m&ocirc;i trường v&ocirc; c&ugrave;ng &yacute; nghĩa. Hy vọng trong thời gian tới, nh&oacute;m từ thiện Hand In Hand Việt H&agrave;n sẽ tiếp tục tham gia những hoạt động &yacute; nghĩa như thế n&agrave;y.</p>\r\n\r\n<p><img alt=\"❤️\" src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t6c/1/16/2764.png\" style=\"height:16px; width:16px\" /> Mọi sự đ&oacute;ng g&oacute;p v&agrave; hỗ trợ được update dưới link: <a href=\"https://l.facebook.com/l.php?u=https%3A%2F%2Ftinyurl.com%2F34brumct%3Ffbclid%3DIwAR1vDHi7Yyh2O6EdOAaMFvvj5V3VgrpRCoVsZGWMF35CmZi1vt2ym_fspqU&amp;h=AT22RPlYVJR_KNfhaboLYxNmPumn6EXZjhzYljKh50hAisLKmqfNWyzmL0HumiuwqdbV5Jtv1B7Sa5NkXats7sicVF0WKhodpFHO3KPtycRksM4Gw8YOfXDXJcChytVepwicDP0l14UfAbOXhi6z&amp;__tn__=-UK-R&amp;c[0]=AT1Jz0jRI1BLERHcaVNqZvBFHp7yt-ZGE4NIvQYiphmmmT2EdDCqow6SRl-OYhMOD6_2ttB940Vvwzrjj-6c_6Zy8gAXCcjzPAsnSpTqCXffO789ZnO4q_B8bmQRg_dSoTGe5ILNgd_QHdNvgiXFlB7w_0fRhA\" rel=\"nofollow noreferrer\" tabindex=\"0\" target=\"_blank\">https://tinyurl.com/34brumct</a></p>\r\n\r\n<p>- Website: <a href=\"https://l.facebook.com/l.php?u=http%3A%2F%2Fwww.handinhandviethan.com%2F%3Ffbclid%3DIwAR1VSH8BaxpJNi7yeasO6NNz34Qk0dVHIddk_iVvKILpy4GonUBCH3bCvxs&amp;h=AT28mP_5G40JyPF-bh-GuwVdrf-etEogey4PV3xyhkt2GA1e_kuQyZPcP7FInxrdTKKMoXyCYKGCsiGUVYR6qcw908WRFoR8heW7szuwwknsSKr19yIe8Z62qWduvl3BaeLQeCzd-v3bmCkvmOvb&amp;__tn__=-UK-R&amp;c[0]=AT1Jz0jRI1BLERHcaVNqZvBFHp7yt-ZGE4NIvQYiphmmmT2EdDCqow6SRl-OYhMOD6_2ttB940Vvwzrjj-6c_6Zy8gAXCcjzPAsnSpTqCXffO789ZnO4q_B8bmQRg_dSoTGe5ILNgd_QHdNvgiXFlB7w_0fRhA\" rel=\"nofollow noreferrer\" tabindex=\"0\" target=\"_blank\">www.handinhandviethan.com</a></p>\r\n\r\n<p>- Kế hoạch g&acirc;y quỹ của nh&oacute;m: <a href=\"https://tinyurl.com/vss6jema?fbclid=IwAR1jYISjGKpbo7ZODC9fOOcsvoHWic8O3YX683mFXyjM0SXZQqRawu-psvE\" rel=\"nofollow noreferrer\" tabindex=\"0\" target=\"_blank\">https://tinyurl.com/vss6jema</a></p>\r\n\r\n<p>- Youtube: <a href=\"https://l.facebook.com/l.php?u=https%3A%2F%2Ftinyurl.com%2Fy27kwstz%3Ffbclid%3DIwAR1dRyvnYxEaf5DYAOEiEwdl0K2uhFW7h1m4sVotmifsCeLV9artF_43eAw&amp;h=AT3HgLZPESY3rcRm07Q7ft_uMXEBpyd842ALbaNkTUMp0FPanKs-mYLoyln45vUcC4MNfoPvE84PCSfT-9WFQWm0hpEG8KmcxtsPL4HBHjWBWbspkk6Pi-qCDVTRtN-n1NSvgx5TT4GFjvrvxt5e&amp;__tn__=-UK-R&amp;c[0]=AT1Jz0jRI1BLERHcaVNqZvBFHp7yt-ZGE4NIvQYiphmmmT2EdDCqow6SRl-OYhMOD6_2ttB940Vvwzrjj-6c_6Zy8gAXCcjzPAsnSpTqCXffO789ZnO4q_B8bmQRg_dSoTGe5ILNgd_QHdNvgiXFlB7w_0fRhA\" rel=\"nofollow noreferrer\" tabindex=\"0\" target=\"_blank\">https://tinyurl.com/y27kwstz</a></p>\r\n\r\n<p>- Email: handinhand.3tviethan@gmail.com</p>', 'Hưởng ứng chương trình “Clean up Son Tra – vì một Sơn Trà xanh” vào sáng Chủ nhật, ngày 29/03/2021 tổ chức bởi Ban Quản lý bán đảo Sơn Trà và các bãi biển, Nhóm từ thiện Hand In Hand Việt Hàn đã huy động được đông đảo thành viên tham gia', 0, 0, NULL, NULL, NULL, 1690131600, 0, 0, 1, NULL, '2023-06-13 14:25:12', '2023-06-13 14:34:20', NULL),
(13, 4, 2, 'Chương trình Giáo dục Nụ Cười 7', 'chuong-trinh-giao-duc-nu-cuoi-7', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686666556/project/chuong-trinh-giao-duc-nu-cuoi-7-701.jpg', '<p>&ldquo;Chương tr&igrave;nh Gi&aacute;o dục Nụ Cười 7 - Gi&uacute;p Trẻ Mồ C&ocirc;i&rdquo; l&agrave; dự &aacute;n kh&ocirc;ng thời hạn nằm trong nh&oacute;m dự &aacute;n hỗ trợ gi&aacute;o dục của Quỹ B&ocirc;ng Sen do Qu&aacute;n Nụ Cười 7 th&agrave;nh lập v&agrave; điều h&agrave;nh. Dự &aacute;n triển khai c&aacute;c hoạt động hỗ trợ gi&aacute;o dục cho trẻ mồ c&ocirc;i tại c&aacute;c trại trẻ mồ c&ocirc;i, nh&agrave; t&igrave;nh thương, m&aacute;i ấm bao gồm cung cấp học bổng ngắn hạn hoặc d&agrave;i hạn; cung cấp học ph&iacute; v&agrave; c&aacute;c chi ph&iacute; li&ecirc;n quan đến việc học tập cho c&aacute;c em học sinh hiếu học.</p>\r\n\r\n<p>Trẻ mồ c&ocirc;i l&agrave; nh&oacute;m trẻ chịu nhiều thiệt th&ograve;i khi kh&ocirc;ng nhận được t&igrave;nh y&ecirc;u thương, sự chăm s&oacute;c từ ba mẹ người th&acirc;n, phải sống trong những gia đ&igrave;nh thay thế như m&aacute;i ấm, nh&agrave; t&igrave;nh thương. C&aacute;c em phải trải qua nhiều mặc cảm, tự ti đặc biệt khi kh&ocirc;ng c&oacute; nhiều điều kiện để đến trường, thiếu thốn kinh ph&iacute; để phục vụ cho qu&aacute; tr&igrave;nh học tập.</p>\r\n\r\n<hr />\r\n<p>&ldquo;Chương tr&igrave;nh Gi&aacute;o dục Nụ Cười 7 &ndash; Gi&uacute;p Trẻ Mồ C&ocirc;i&rdquo; l&agrave; dự &aacute;n kh&ocirc;ng thời hạn nằm trong nh&oacute;m dự &aacute;n hỗ trợ gi&aacute;o dục của Quỹ B&ocirc;ng Sen do Qu&aacute;n Nụ Cười 7 th&agrave;nh lập v&agrave; điều h&agrave;nh. Dự &aacute;n triển khai c&aacute;c hoạt động hỗ trợ gi&aacute;o dục cho trẻ mồ c&ocirc;i tại c&aacute;c trại trẻ mồ c&ocirc;i, nh&agrave; t&igrave;nh thương, m&aacute;i ấm bao gồm cung cấp học bổng ngắn hạn hoặc d&agrave;i hạn; cung cấp học ph&iacute; v&agrave; c&aacute;c chi ph&iacute; li&ecirc;n quan đến việc học tập cho c&aacute;c em học sinh hiếu học.</p>\r\n\r\n<hr />\r\n<p>Dự &aacute;n Gi&aacute;o dục Nụ Cười 7- Gi&uacute;p Trẻ Mồ C&ocirc;i gồm 3 chương tr&igrave;nh miễn ph&iacute;:<br />\r\n<strong>(1)</strong>&nbsp;Lo việc học cho trẻ em mồ c&ocirc;i tại c&aacute;c trại trẻ mồ c&ocirc;i, nh&agrave; t&igrave;nh thương, m&aacute;i ấm (gồm học ph&iacute;, s&aacute;ch gi&aacute;o khoa, đồng phục, &hellip;).<br />\r\n<strong>(2)</strong>&nbsp;Cung cấp suất ăn dinh dưỡng cho học sinh tại c&aacute;c m&aacute;i ấm (hiện đang lo cơm trưa v&agrave;o c&aacute;c ng&agrave;y thứ Hai, Tư, S&aacute;u cho một số m&aacute;i ấm t&igrave;nh thương như m&aacute;i ấm tại ch&ugrave;a Từ Hạnh, m&aacute;i ấm D&ograve;ng mến th&aacute;nh gi&aacute;&hellip;.)<br />\r\n<strong>(3)</strong>&nbsp;X&acirc;y dựng đội ngũ nh&acirc;n vi&ecirc;n Dự &aacute;n c&oacute; kinh nghiệm sự phạm đến giảng dạy tại một số lớp học t&igrave;nh thương, gi&aacute;o dục về kiến thức, đạo đức cũng như hỗ trợ c&aacute;c em về mặt tậm l&yacute; l&agrave;m điểm tựa cho c&aacute;c em.</p>\r\n\r\n<hr />\r\n<p>&Ocirc;ng Nguyễn Minh Nghĩa &ndash; Quản l&yacute; Qu&aacute;n cơm X&atilde; hội Nụ Cười 7 sẽ nhận nhiệm vụ Chủ nhiệm Dự &aacute;n &ldquo;Chương tr&igrave;nh Gi&aacute;o dục Nụ Cười 7 &ndash; Gi&uacute;p Trẻ Mồ C&ocirc;i&rdquo;</p>\r\n\r\n<p>Li&ecirc;n hệ: 0902 515 490 (Mr. Minh Nghĩa) hoặc 0363 477 066 (Ms. Ho&agrave;ng Anh)</p>\r\n\r\n<hr />\r\n<p>Mọi đ&oacute;ng góp cho Dự &aacute;n Chương tr&igrave;nh Gi&aacute;o dục Nụ Cười 7- Gi&uacute;p Trẻ Mồ C&ocirc;i xin vui l&ograve;ng gửi về t&agrave;i khoản:<br />\r\n<em>T&ecirc;n TK: Quỹ từ thiện B&ocirc;ng Sen</em><br />\r\n<em>Số TK: 0602.0924.3949 Ng&acirc;n h&agrave;ng Sacombank &ndash; Chi nh&aacute;nh T&acirc;n Định_ PGD V&otilde; Thị S&aacute;u.</em></p>', 'Chương trình Giáo dục Nụ Cười 7 - Giúp Trẻ Mồ Côi” là dự án không thời hạn nằm trong nhóm dự án hỗ trợ giáo dục của Quỹ Bông Sen do Quán Nụ Cười 7 thành lập và điều hành. Dự án triển khai các hoạt động hỗ trợ giáo dục cho trẻ mồ côi tại các trại trẻ mồ côi,', 10000000, 0, 'Quán Nụ Cười 7', '0964114749', NULL, 1689181200, 3500000, 0, 1, NULL, '2023-06-13 14:29:16', '2023-06-13 14:35:28', NULL),
(14, 4, 2, 'CHƯƠNG TRÌNH NGƯỜI BẠN LỚN', 'chuong-trinh-nguoi-ban-lon', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686666772/project/chuong-trinh-nguoi-ban-lon-9549.jpg', '<p>Người bạn lớn l&agrave; một dự &aacute;n c&oacute; mục đ&iacute;ch gi&uacute;p đỡ Trẻ em kh&ocirc;ng c&ograve;n cha hoặc mẹ hoặc cả hai, đặc biệt l&agrave; Trẻ nhỏ chịu mất m&aacute;t n&agrave;y do đại dịch Covid-19. Chương tr&igrave;nh thiết lập một mạng lưới Nh&acirc;n vi&ecirc;n đ&oacute;ng vai tr&ograve; l&agrave; &ldquo;người anh tinh thần&rdquo;, &ldquo;người chị tinh thần&rdquo; để chăm s&oacute;c c&aacute;c em.</p>\r\n\r\n<p>Khởi nguồn từ 9 trẻ tại Phường 8 Quận 4 v&agrave;o năm 2021, đến nay ch&uacute;ng t&ocirc;i đ&atilde; hỗ trợ 32 trẻ tại Quận 4, B&igrave;nh Ch&aacute;nh v&agrave; G&ograve; Vấp.</p>\r\n\r\n<p>Trong hơn một năm triển khai chương tr&igrave;nh, đồng h&agrave;nh c&ugrave;ng trẻ v&agrave; gia đ&igrave;nh ch&uacute;ng t&ocirc;i hiểu được những kh&oacute; khăn, chật vật của c&aacute;c gia đ&igrave;nh sau khi trải qua một biến cố to lớn. Kh&ocirc;ng chỉ về kinh tế m&agrave; những tổn thương về mặt tinh thần, cảm x&uacute;c, đời sống dường như vẫn c&ograve;n tiếp diễn.</p>\r\n\r\n<p>Đối với trẻ, ch&uacute;ng t&ocirc;i tập trung v&agrave;o hỗ trợ học bổng để duy tr&igrave; việc học của trẻ tại trường, hỗ trợ dinh dưỡng cho những trẻ đ&atilde; mất đi nguồn sữa mẹ từ khi mới lọt l&ograve;ng. B&ecirc;n cạnh đ&oacute;, việc quan t&acirc;m, chăm s&oacute;c về mặt tinh thần của trẻ như sinh nhật, lễ, tết cũng được quan t&acirc;m. Quan trọng hơn hết, Người Bạn Lớn cũng đ&oacute;ng vai tr&ograve; người anh, người chị, người bạn để trẻ được chia sẻ những t&acirc;m tư, những vấn trẻ đang gặp phải ở từng giai đoạn ph&aacute;t triển, đặc biệt tuổi teen.</p>\r\n\r\n<p>T&iacute;nh đến nay, ch&uacute;ng t&ocirc;i đang hỗ trợ chi ph&iacute; học tập h&agrave;ng cho 21 trẻ về học ph&iacute;, đồ d&ugrave;ng học tập, v&eacute; xe bu&yacute;t,&hellip;. Về dinh dưỡng h&agrave;ng th&aacute;ng cho 13 trẻ. C&oacute; 2 trẻ được hỗ trợ về chi ph&iacute; kh&aacute;m-điều trị bệnh. 32 trẻ đều được cập nhật th&ocirc;ng tin v&agrave; hỗ trợ khi cần thiết, tổ chức sinh nhật v&agrave; tặng qu&agrave; c&aacute;c dịp đặc biệt trong năm: Gi&aacute;ng sinh, Tết&hellip;.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ hỗ trợ cho trẻ, Người Bạn Lớn c&ograve;n l&agrave; nơi m&agrave; người chăm s&oacute;c trẻ được chia sẻ, được hướng dẫn hỗ trợ, tư vấn khi cần thiết. Khi gia đ&igrave;nh bị mất đi một người th&acirc;n, mất đi một người lao động ch&iacute;nh th&igrave; người ở lại kh&ocirc;ng chỉ kh&oacute; khăn về mặt kinh tế m&agrave; đ&ocirc;i khi việc giao tiếp, chăm s&oacute;c c&aacute;c con cũng l&agrave; một kh&oacute; khăn khi những c&ocirc;ng việc đ&oacute; vốn dĩ được chăm lo bởi người chồng/người vợ của m&igrave;nh. Kh&ocirc;ng &iacute;t lần ch&uacute;ng t&ocirc;i thấy được những giọt nước mắt rơi khi người chăm s&oacute;c chia sẻ về hiện tại, mặc d&ugrave; đ&atilde; qua một năm gắng gượng để chăm s&oacute;c c&aacute;c con.</p>\r\n\r\n<p><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/z3134453750171_455720d4ef705fde426deec9b24269d4/\" rel=\"attachment wp-att-16110\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/z3134453750171_455720d4ef705fde426deec9b24269d4.jpg\" style=\"height:443px; width:591px\" /></a><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/z3134453470135_dcdd18eb4446b36692f71cd4dd72a89e/\" rel=\"attachment wp-att-16109\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/z3134453470135_dcdd18eb4446b36692f71cd4dd72a89e.jpg\" style=\"height:443px; width:591px\" /></a></p>\r\n\r\n<p><em>Những chuyến thăm gia đ&igrave;nh v&agrave; hỗ trợ học bổng đầu ti&ecirc;n</em></p>\r\n\r\n<p><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/z3050425476105_e84196ce6c7b29c6a01e49be28cc2d0d/\" rel=\"attachment wp-att-16107\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/z3050425476105_e84196ce6c7b29c6a01e49be28cc2d0d-rotated.jpg\" style=\"height:443px; width:591px\" /></a><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/bc2/\" rel=\"attachment wp-att-16104\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/BC2.png\" style=\"height:439px; width:595px\" /></a><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/z3993113880154_193b70ee49bf30269db4ab1791b0af12/\" rel=\"attachment wp-att-16111\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/z3993113880154_193b70ee49bf30269db4ab1791b0af12.jpg\" style=\"height:443px; width:591px\" /></a><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/q4/\" rel=\"attachment wp-att-16106\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/Q4.png\" style=\"height:443px; width:591px\" /></a></p>\r\n\r\n<p><em>Thăm trẻ h&agrave;ng th&aacute;ng, tổ chức sinh nhật, chương tr&igrave;nh họp mặt cuối năm</em></p>\r\n\r\n<p><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/gv1/\" rel=\"attachment wp-att-16105\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/GV1.png\" style=\"height:523px; width:500px\" /></a><a href=\"https://quybongsen.org/du-an/chuong-trinh-nguoi-ban-lon-ho-tro-tre-bi-anh-huong-boi-dich-covid-19/bc1/\" rel=\"attachment wp-att-16103\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/12/BC1.png\" style=\"height:502px; width:376px\" /></a></p>\r\n\r\n<p><em>Mặc d&ugrave; chưa c&oacute; dịp gặp mẹ nhưng c&aacute;c con vẫn được mọi người y&ecirc;u thương v&agrave; chăm s&oacute;c</em></p>\r\n\r\n<p><strong>Một số c&acirc;u chuyện m&agrave; ch&uacute;ng t&ocirc;i đang đồng h&agrave;nh</strong></p>\r\n\r\n<p>&ldquo;C&oacute; người mẹ ở B&igrave;nh Ch&aacute;nh phải b&aacute;n từng t&ocirc; hủ tiếu để chăm s&oacute;c hai con đang tuổi đi học, c&oacute; những l&uacute;c tủi th&acirc;n, mệt mỏi cũng cố gạt nước mắt đi để l&agrave;m chỗ dựa cho 2 bạn nhỏ. Chị thương con nhưng đ&ocirc;i khi v&igrave; g&aacute;nh nặng mưu sinh m&agrave; chị kh&ocirc;ng c&oacute; thời gian để chia sẻ những c&acirc;u chuyện h&agrave;ng ng&agrave;y hay b&agrave;y tỏ y&ecirc;u thương với con m&igrave;nh, NBL lại trở th&agrave;nh cầu nối, chia sẻ với c&aacute;c trẻ v&agrave; mẹ để cả hai đều hiểu được sự y&ecirc;u thương d&agrave;nh cho nhau.&rdquo;</p>\r\n\r\n<p>&ldquo;C&oacute; người mẹ ở Quận 4 đau bệnh v&agrave; phải theo d&otilde;i tại bệnh viện nhưng tiền học phụ đạo cho con chị c&ograve;n phải c&acirc;n nhắc th&igrave; việc đi kh&aacute;m bệnh dường như chị phải bỏ qua v&agrave; chịu đựng những bất thường về sức khỏe. NBL sẽ trở th&agrave;nh cầu nối đến những nguồn lực kh&aacute;c để hỗ trợ việc kh&aacute;m-chữa bệnh cho người chăm s&oacute;c. Trẻ chỉ c&ograve;n một người th&acirc;n duy nhất l&agrave; mẹ, việc đồng h&agrave;nh c&ugrave;ng cả 2 mẹ con ch&uacute;ng t&ocirc;i sẽ cố gắng hết sức c&oacute; thể.&rdquo;</p>\r\n\r\n<p>&ldquo;C&oacute; &ocirc;ng bố ở G&ograve; Vấp ch&ecirc;nh v&ecirc;nh khi mất đi người vợ tần tảo, đảm đang trong tất cả c&aacute;c c&ocirc;ng việc của gia đ&igrave;nh. Vợ rời bỏ gia đ&igrave;nh sau trận đại dịch, để lại anh chăm s&oacute;c đưa con 10 tuổi v&agrave; 1 b&eacute; sơ sinh đỏ hỏn. Từ bỏ c&ocirc;ng việc ổn định trước đ&acirc;y để t&igrave;m một c&ocirc;ng việc tự do để c&oacute; thể linh hoạt giờ giấc đưa đ&oacute;n con.&rdquo;</p>', 'Người bạn lớn là một dự án có mục đích giúp đỡ Trẻ em không còn cha hoặc mẹ hoặc cả hai, đặc biệt là Trẻ nhỏ chịu mất mát này do đại dịch Covid-19.', 10000000, 200000, 'Hỗ trợ trẻ bị ảnh hưởng bởi COVID-19', '0964114749', NULL, 1692810000, 2500000, 0, 1, NULL, '2023-06-13 14:32:52', '2023-06-13 14:10:50', NULL),
(15, 3, 2, 'Quán cơm Xã hội Nụ Cười 8', 'quan-com-xa-hoi-nu-cuoi-8', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686667097/project/quan-com-xa-hoi-nu-cuoi-8-2406.jpg', '<p>Địa chỉ: Số 846, đường Huỳnh Tấn Ph&aacute;t, P. T&acirc;n Ph&uacute;, Quận 7, TP.HCM. Số điện thoại: 0901 315826 (Mr. Nhi&ecirc;n) hoặc 077 741 5828 (Ms. V&acirc;n). Ng&agrave;y hoạt động thứ 2-4-6 trong tuần.</p>\r\n\r\n<p>Qu&aacute;n cơm Nụ Cười 8 nằm trong chuỗi qu&aacute;n cơm 2.000đ, thuộc dự &aacute;n trợ gi&uacute;p suất ăn gi&aacute; rẻ của Quỹ từ thiện B&ocirc;ng Sen. Qu&aacute;n được khai trương v&agrave;o ng&agrave;y 26/1/2019, phục vụ b&agrave; con lao động ngh&egrave;o v&agrave; học sinh, sinh vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn v&agrave;o c&aacute;c ng&agrave;y thứ Hai &ndash; Tư &ndash; S&aacute;u trong tuần. Qu&aacute;n được điều h&agrave;nh bởi Ban Li&ecirc;n lạc Thầy C&ocirc; v&agrave; Cựu học sinh Petrus K&yacute; &amp; L&ecirc; Hồng Phong.</p>\r\n\r\n<hr />\r\n<p>Chủ nhiệm qu&aacute;n: Mr. V&otilde; Anh Dũng</p>\r\n\r\n<p>Số 846, đường Huỳnh Tấn Ph&aacute;t, P. T&acirc;n Ph&uacute;, Quận 7, TP.HCM.</p>\r\n\r\n<p>Số điện thoại: 0901 315826 (Mr. Nhi&ecirc;n) hoặc 077 741 5828 (Ms. V&acirc;n).</p>\r\n\r\n<p>Email: dcn20052006@yahoo.com</p>\r\n\r\n<p>TK: Đặng Chiếu Nhi&ecirc;n.STK: 223310101000028 Ng&acirc;n H&agrave;ng Thương Mại Cổ Phần Xuất Nhập Khẩu Việt Nam (EXIMBANK) &ndash; Ph&ograve;ng giao dịch Phan Đ&igrave;nh Ph&ugrave;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2019/02/nc3.jpg\" style=\"height:720px; width:1280px\" /></p>', 'Quán cơm Nụ Cười 8 nằm trong chuỗi quán cơm 2.000đ, thuộc dự án trợ giúp suất ăn giá rẻ của Quỹ từ thiện Bông Sen. Quán được khai trương vào ngày 26/1/2019,', 15000000, 0, 'Quán cơm Xã hội Nụ Cười', '0964114749', NULL, 1691082000, 0, 0, 1, NULL, '2023-06-13 14:38:17', '2023-06-13 14:38:17', NULL),
(16, 3, 2, 'PHỞ CHO MỌI NGƯỜI', 'pho-cho-moi-nguoi', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686667200/project/pho-cho-moi-nguoi-3505.jpg', '<p>Một hoạt động đặc biệt trong dự &aacute;n trợ gi&uacute;p suất ăn gi&aacute; rẻ của Quỹ Từ thiện B&ocirc;ng Sen l&agrave; &ldquo;Phở Cho Mọi Người&rdquo; do nh&oacute;m bạn &ldquo;Ng&agrave;y thứ Năm Hạnh Ph&uacute;c&rdquo; v&agrave; một v&agrave;i doanh nghiệp, tổ chức t&agrave;i trợ.</p>\r\n\r\n<p>Dự &aacute;n &ldquo;Phở Cho Mọi Người&rdquo; l&agrave; chương tr&igrave;nh t&agrave;i trợ chi ph&iacute; b&aacute;n m&oacute;n nước cho c&aacute;c qu&aacute;n cơm Nụ Cười của nh&oacute;m bạn &ldquo;Ng&agrave;y thứ Năm Hạnh Ph&uacute;c&rdquo; do anh Trần Viết Hu&acirc;n l&agrave;m chủ nhiệm c&ugrave;ng một v&agrave;i doanh nghiệp, tổ chức t&agrave;i trợ. H&agrave;ng th&aacute;ng mỗi qu&aacute;n sẽ c&oacute; từ 1 &ndash; 4 ng&agrave;y nấu c&aacute;c m&oacute;n nước như: Phở, Mỳ g&agrave; tiềm, Hủ tiếu Nam Vang, B&uacute;n B&ograve; Huế&hellip; để phục vụ thực kh&aacute;ch, mỗi phần ăn chỉ c&oacute; gi&aacute; 1.000 đồng bao gồm lu&ocirc;n m&oacute;n tr&aacute;ng miệng v&agrave; nước uống. Những ng&agrave;y n&agrave;y thường được b&agrave; con ủng hộ rất đ&ocirc;ng v&agrave; nhiệt t&igrave;nh v&igrave; đem lại cảm gi&aacute;c lạ miệng, ngon, rẻ&hellip;</p>\r\n\r\n<p>Chủ nhiệm Dự &aacute;n: &Ocirc;ng Trần Viết Hu&acirc;n<br />\r\nT&agrave;i khoản: Quỹ Từ thiện B&ocirc;ng Sen &ndash; số Tk: 0371 0004 81131&ndash; Ng&acirc;n h&agrave;ng thương mại cổ phần ngoại thương Việt Nam (Vietcombank) &ndash; Chi nh&aacute;nh T&acirc;n Định</p>\r\n\r\n<p>Xem th&ecirc;m cập nhật đ&oacute;ng g&oacute;p cho dự &aacute;n:&nbsp;<strong><a href=\"http://forum.quybongsen.org/yaf_topics30_Cap-nhat-dong-gop-24h-du-an-y-te-va-khong-chi-dinh.aspx\">Tại đ&acirc;y</a></strong></p>\r\n\r\n<p><img alt=\"4(3)\" src=\"https://quybongsen.org/wp-content/uploads/2017/12/43-300x225.jpg\" style=\"height:540px; width:720px\" /></p>\r\n\r\n<p><img alt=\"nc4\" src=\"https://quybongsen.org/wp-content/uploads/2017/12/nc4-300x225.jpg\" style=\"height:540px; width:720px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2019/08/a-e1566268681574.jpg\" style=\"height:479px; width:720px\" /><br />\r\n<img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2017/12/IMG_20131226_142216-t%C3%B4-22222h-e1566269120543.jpg\" style=\"height:540px; width:720px\" /></p>', 'Một hoạt động đặc biệt trong dự án trợ giúp suất ăn giá rẻ của Quỹ Từ thiện Bông Sen là “Phở Cho Mọi Người” do nhóm bạn “Ngày thứ Năm Hạnh Phúc” và một vài doanh nghiệp,', 0, 0, NULL, NULL, NULL, 1694451600, 0, 0, 1, NULL, '2023-06-13 14:40:01', '2023-06-13 14:40:32', NULL),
(17, 2, 2, 'GIÚP TRẺ MỒ CÔI TẠI MÁI ẤM', 'giup-tre-mo-coi-tai-mai-am', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686667568/project/giup-tre-mo-coi-tai-mai-am-7993.jpg', '<p>Th&ecirc;m một dự &aacute;n kh&ocirc;ng thời hạn nằm trong chương tr&igrave;nh hỗ trợ gi&aacute;o dục trẻ em tỉnh Quảng Trị. Mỗi th&aacute;ng, Qũy B&ocirc;ng Sen sẽ cần vận động 8 triệu đồng hỗ trợ sữa cho M&aacute;i Ấm L&acirc;m B&iacute;ch ở Đ&ocirc;ng H&agrave; v&agrave; 15 triệu cho M&aacute;i Ấm Cam Lộ để hỗ trợ sữa v&agrave; thực phẩm cho những trẻ mồ c&ocirc;i đang được nu&ocirc;i dưỡng tại đ&acirc;y.</p>\r\n\r\n<p dir=\"auto\">Hậu Covid</p>\r\n\r\n<p dir=\"auto\">Tr&ecirc;n mỗi người bệnh c&oacute; thể để lại v&agrave;i di chứng, nhưng tr&ecirc;n nền kinh tế của cả nước, dấu t&iacute;ch Covid để lại cho nền kinh tế qu&aacute; nặng nề.</p>\r\n\r\n<p>Chuyến khảo s&aacute;t miền Trung th&aacute;ng 7 vừa qua, ch&uacute;ng t&ocirc;i ghi nhận phần lớn người d&acirc;n ngh&egrave;o hơn hẳn so với trước đ&acirc;y. C&oacute; thể c&aacute;c khu du lịch, c&aacute;c tụ điểm vui chơi kh&ocirc;ng thấy biểu hiện của sức mua suy giảm bởi một số thị d&acirc;n kh&ocirc;ng bị mất việc, c&ograve;n phần lớn người lao động ngh&egrave;o về qu&ecirc;, cuộc mưu sinh trở n&ecirc;n kh&oacute; khăn hơn trước, gi&aacute; cả leo thang ch&oacute;ng mặt.Ở những cơ sở từ thiện x&atilde; hội tại c&aacute;c tỉnh ngh&egrave;o như Quảng Trị, Gia Lai v.v&hellip; cuộc sống v&ocirc; c&ugrave;ng kh&oacute; khăn. Người đến thăm viếng v&agrave; tặng qu&agrave; &iacute;t đi hẳn, giảm s&uacute;t rất nhiều khiến nguồn thu kh&ocirc;ng c&oacute;. C&aacute;c ma sơ chạy vạy đủ c&aacute;ch, chế biến tr&agrave; gừng, bột ngũ cốc b&aacute;n v&agrave;i chục ng&agrave;n mong cải thiện bữa ăn cho c&aacute;c ch&aacute;u mồ c&ocirc;i hoặc người d&acirc;n tộc thất học.</p>\r\n\r\n<p dir=\"auto\">C&agrave;ng xa trung t&acirc;m, c&agrave;ng &iacute;t người đến th&igrave; c&agrave;ng kh&oacute; khăn. M&aacute;i ấm Cam Lộ ở Quảng Trị, nơi Qu&aacute;n Y&ecirc;n Vui trợ gi&uacute;p thực phẩm khoảng 9 triệu mỗi th&aacute;ng cho 32 ch&aacute;u, c&aacute;c ma sơ cũng rất chật vật t&igrave;m th&ecirc;m nguồn n&agrave;y nguồn kia cho c&aacute;c em. Cả đo&agrave;n ch&uacute;ng t&ocirc;i v&ocirc; c&ugrave;ng x&uacute;c động khi sơ HĐ tr&ograve;n mắt, ngẩn người v&igrave; ngạc nhi&ecirc;n vỡ &ograve;a niềm vui khi nghe ch&uacute;ng t&ocirc;i ngỏ lời gi&uacute;p th&ecirc;m 6 triệu nữa mỗi th&aacute;ng để c&aacute;c ch&aacute;u c&oacute; thể uống sữa. Sữa! Ở nơi xa x&ocirc;i n&agrave;y trẻ con trong M&aacute;i ấm từ thiện được uống sữa l&agrave; điều qu&aacute; hoang đường.</p>\r\n\r\n<p>Vậy l&agrave; th&ecirc;m một dự &aacute;n kh&ocirc;ng thời hạn nằm trong chương tr&igrave;nh hỗ trợ gi&aacute;o dục trẻ em tỉnh Quảng Trị. Mỗi th&aacute;ng sẽ cần vận động 8 triệu đồng cho M&aacute;i Ấm L&acirc;m B&iacute;ch ở Đ&ocirc;ng H&agrave; v&agrave; 15 triệu đồng cho M&aacute;i Ấm Cam Lộ. Ch&uacute;ng t&ocirc;i chỉ l&agrave; người giao nhận, l&agrave; shipper vận chuyển l&ograve;ng tốt của qu&yacute; nh&agrave; hảo t&acirc;m đến những nơi cần thiết. Nhận một lời c&aacute;m ơn từ c&aacute;c ma sơ cũng đ&atilde; l&agrave; qu&aacute; ngại ng&ugrave;ng.</p>\r\n\r\n<p dir=\"auto\">lvc, trong nh&oacute;m chăm s&oacute;c QBS</p>\r\n\r\n<p>Mọi sự đồng h&agrave;nh c&ugrave;ng dự &aacute;n c&oacute; thể gửi về:</p>\r\n\r\n<p>T&ecirc;n T&agrave;i Khoản: Quỹ Từ Thiện B&ocirc;ng Sen</p>\r\n\r\n<p>Số t&agrave;i khoản VND:</p>\r\n\r\n<p>TK1: 6420201012822 &ndash; Ng&acirc;n h&agrave;ng Agribank &ndash; Chi nh&aacute;nh 10</p>\r\n\r\n<p>TK2: 0371000481127 &ndash; Ng&acirc;n h&agrave;ng Vietcombank &ndash; Chi nh&aacute;nh T&acirc;n Định</p>\r\n\r\n<p>TK3: 508317 &ndash; Ng&acirc;n h&agrave;ng ACB &ndash; Chi Nh&aacute;nh S&agrave;i G&ograve;n</p>\r\n\r\n<p>TK4: 18610000165426 &ndash; Ng&acirc;n h&agrave;ng BIDV &ndash; Chi nh&aacute;nh H&agrave;m Nghi.</p>\r\n\r\n<p>Nội dung:&nbsp;<strong>T&ecirc;n Nh&agrave; hảo t&acirc;m + ủng hộ Dự &aacute;n trẻ mồ c&ocirc;i M&aacute;i ấm</strong></p>\r\n\r\n<p>Một số h&igrave;nh ảnh sinh hoạt tại 2 m&aacute;i ấm m&agrave; ch&uacute;ng t&ocirc;i ghi nhận được</p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/292556345_5640591772641092_6067964686078809376_n.jpg\" style=\"height:1276px; width:956px\" /></p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/292676111_5640595325974070_2247929270877854814_n.jpg\" style=\"height:956px; width:1276px\" /></p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/293424105_5640594029307533_5920115371596682741_n.jpg\" style=\"height:1280px; width:960px\" /></p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/293487657_5640597452640524_7705078184158091565_n.jpg\" style=\"height:1600px; width:1200px\" /></p>\r\n\r\n<p dir=\"auto\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2022/07/293135997_5640598289307107_3189005968200839937_n.jpg\" style=\"height:1600px; width:1200px\" /></p>', 'Thêm một dự án không thời hạn nằm trong chương trình hỗ trợ giáo dục trẻ em tỉnh Quảng Trị. Mỗi tháng, Qũy Bông Sen sẽ cần vận động 8 triệu đồng hỗ trợ sữa cho Mái Ấm Lâm Bích ở Đông Hà và 15 triệu cho Mái Ấm Cam Lộ để hỗ trợ sữa và thực phẩm cho những trẻ mồ côi đang được nuôi dưỡng tại đây.', 5000000, 0, 'Trung tâm bảo trợ trẻ em mồ côi Đà Nẵng', '0964114749', NULL, 1689786000, 3000000, 0, 1, NULL, '2023-06-13 14:46:08', '2023-06-13 14:46:49', NULL),
(18, 1, 2, 'Hơi ấm ngày đông', 'hoi-am-ngay-dong', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686680032/project/hoi-am-ngay-dong-7927.jpg', '<p>ss</p>', 'hi', 20000000, 0, 'Nhóm từ thiện Hand In Hand Việt - Hàn', '0946234470', NULL, 1700499600, 0, 0, 0, NULL, '2023-06-13 18:13:52', '2023-06-13 12:42:47', '2023-06-13 12:42:47'),
(19, 4, 2, 'Clean Up Sơn Trà 3', 'clean-up-son-tra-3', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686681412/project/clean-up-son-tra-3-3192.jpg', '<p>sss</p>', 'ss', 10000000, 0, 'Mái Ấm Cam Lộ', '0964114749', NULL, 1688662800, 0, 0, 0, NULL, '2023-06-13 18:36:52', '2023-06-13 12:42:57', '2023-06-13 12:42:57'),
(20, 2, 2, 'NGƯỜI MẸ NGHỊ LỰC NUÔI CON SAU BIẾN CỐ', 'nguoi-me-nghi-luc-nuoi-con-sau-bien-co', 'https://res.cloudinary.com/dcugpagjy/image/upload/v1686685147/project/nguoi-me-nghi-luc-nuoi-con-sau-bien-co-3024.png', '<p>Chồng mất sau dịch Covid-19, một m&igrave;nh chị cố gắng vực dậy tinh thần để tiếp tục nu&ocirc;i dưỡng đứa con trai. Cuộc sống của hai mẹ con vừa ổn định th&igrave; chị ph&aacute;t hiện bị u v&uacute;. Cuộc sống của hai mẹ con vốn đ&atilde; chật vật, nay ph&aacute;t sinh th&ecirc;m chi ph&iacute; theo d&otilde;i v&agrave; điều trị bệnh th&igrave; kh&oacute; khăn lại gấp bội phần.</p>\r\n\r\n<p>Chị La T.D, sinh năm 1984 tại Tp.HCM. Sau khi chồng qua đời, chị vẫn duy tr&igrave; c&ocirc;ng việc b&aacute;n nước giải kh&aacute;t ở ven đường để trang trải chi ph&iacute; sinh hoạt của hai mẹ con. Con chị đang được hỗ trợ bởi chương tr&igrave;nh Người Bạn Lớn tại Quỹ B&ocirc;ng Sen n&ecirc;n qua qu&aacute; tr&igrave;nh hỗ trợ, ch&uacute;ng t&ocirc;i hiểu những kh&oacute; khăn m&agrave; họ đ&atilde; trải qua để trở lại cuộc sống như hiện nay. Mất đi người chồng, chị trở th&agrave;nh lao động ch&iacute;nh, vừa l&agrave;m mẹ vừa l&agrave;m ba để đứa con trai nương tựa. Khi ph&aacute;t hiện sức khỏe gặp vấn đề, chị chần chừ m&atilde;i kh&ocirc;ng d&aacute;m đi kh&aacute;m v&igrave; sợ bệnh sợ tốn tiền điều trị trong khi 2 mẹ con đang d&egrave; xẻng từng khoản chi. Được sự động vi&ecirc;n, chị đi kh&aacute;m th&igrave; được chẩn đo&aacute;n l&agrave; u v&uacute; nhưng vẫn may mắn l&agrave; u l&agrave;nh n&ecirc;n định kỳ sẽ đến bệnh viện theo d&otilde;i t&aacute;i kh&aacute;m, lấy thuốc, chưa ảnh hưởng nhiều đến c&ocirc;ng việc mưu sinh. Mỗi lần kh&aacute;m, mua thuốc chi ph&iacute; khoảng 1-2 triệu đồng nhưng với chị đ&oacute; l&agrave; một khoản chi rất lớn với t&igrave;nh h&igrave;nh của hai mẹ con.</p>\r\n\r\n<p><a href=\"https://quybongsen.org/du-an/ky-537-nguoi-me-nghi-luc-nuoi-con-sau-bien-co/c-dung-jpg/\" rel=\"attachment wp-att-16307\"><img alt=\"\" src=\"https://quybongsen.org/wp-content/uploads/2023/02/C-Dung.jpg.png\" style=\"height:1440px; width:1920px\" /></a></p>\r\n\r\n<p>Sau nụ cười&nbsp; tươi, một tinh thần lạc quan đ&oacute;n nhận những g&igrave; đang đến với chị trong cuộc sống th&igrave; ch&uacute;ng t&ocirc;i hiểu được đ&oacute; l&agrave; một người phụ nữ nghị lực. Ch&iacute;nh từ t&igrave;nh y&ecirc;u thương d&agrave;nh cho con trai, niềm thương nhớ người chồng l&agrave; động lực của chị.</p>\r\n\r\n<p>Trước ho&agrave;n cảnh của chị D, để chị an t&acirc;m kh&aacute;m bệnh, ch&uacute;ng t&ocirc;i hy vọng c&oacute; thể vận động g&oacute;i hỗ trợ 20.000.000 đồng để chị c&oacute; thể trang trải chi ph&iacute; t&aacute;i kh&aacute;m, theo d&otilde;i bệnh trong thời gian sắp tới. Con chị, đứa trẻ m&agrave; Quỹ ch&uacute;ng t&ocirc;i đang hỗ trợ chăm s&oacute;c đ&atilde; đột ngột mất đi người ba n&ecirc;n ch&uacute;ng t&ocirc;i hiểu được mẹ l&agrave; tất cả của trẻ trong cuộc sống n&ecirc;n ch&uacute;ng t&ocirc;i hy vọng rằng với sự hỗ trợ n&agrave;y c&ugrave;ng với tinh thần lạc quan, sức khỏe của chị D sẽ được cải thiện t&iacute;ch cực. Để hỗ trợ chị, Quỹ sẽ hỗ trợ đ&uacute;ng theo số tiền tr&ecirc;n h&oacute;a đơn thanh to&aacute;n của chị khi t&aacute;i kh&aacute;m v&agrave; trừ dần trong 20 triệu đồng.</p>\r\n\r\n<p>Để tr&aacute;nh rủi ro bệnh nh&acirc;n bị lừa qua điện thoại, Quỹ kh&ocirc;ng c&ocirc;ng khai số điện thoại bệnh nh&acirc;n. Mọi đ&oacute;ng g&oacute;p cho bệnh nh&acirc;n vui l&ograve;ng li&ecirc;n hệ đến Quỹ Từ thiện B&ocirc;ng Sen &ndash; Lầu 5 số 7- 9 -11 Mai Thị Lựu, P.Đa Kao, Q1, TP.HCM &ndash; ĐT: (028) 39107612</p>\r\n\r\n<p>C&aacute;c khoản đ&oacute;ng g&oacute;p của c&aacute;c nh&agrave; hảo t&acirc;m sẽ được chuyển đ&uacute;ng đến bệnh nh&acirc;n được chỉ định. Tuy nhi&ecirc;n, để tr&aacute;nh t&igrave;nh trạng qu&aacute; nhiều nguồn dồn về một mối m&agrave; c&aacute;c trường hợp k&eacute;m may mắn kh&aacute;c kh&ocirc;ng c&oacute; được, ch&uacute;ng t&ocirc;i xin ph&eacute;p nếu tổng số tiền quy&ecirc;n g&oacute;p được nhiều hơn 120% số cần gi&uacute;p đỡ ch&uacute;ng t&ocirc;i chỉ chuyển tối đa 120% cho người bệnh v&agrave; phần dư ra sẽ chuyển qua gi&uacute;p đỡ kỳ tiếp theo.</p>', 'Chồng mất sau dịch Covid-19, một mình chị cố gắng vực dậy tinh thần để tiếp tục nuôi dưỡng đứa con trai.', 10000000, 0, 'Bông Sen Vàng', '0964114749', NULL, 1688835600, 0, 0, 1, NULL, '2023-06-13 19:39:07', '2023-06-13 12:43:03', NULL);

